css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://dragcave.net") {

.csort2 img{
background: #999999 !important;
}

td, tr, th img{
background: #dbdbdb !important;
}

table, td, td{
text-align: center !important;
font-weight: normal !important;
font-size: 13px !important;
border-spacing: 5px !important;
}


INPUT {
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

TEXTAREA {
color:#333 !important;
font:8pt Trebuchet MS !important;
text-transform: inherit !important;
}

html,body {
	padding: 0 !important;
	margin: 0 !important;
}
body {
	color: #382D3B !important;
	background: #b9b9b9 url("http://i41.tinypic.com/6ny0sj.png") repeat !important;
	font-size: 9pt !important;
	font-family: Segoe UI !important;
	color: #382D3B !important;
}
h1,h2 {
	padding: 0 !important;
	margin: 0 !important;
}

b {     
        color:#999999 !important;font:11px georgia !important;
        font-style:italic !important;
        font-weight:bold !important;letter-spacing:-.5pt !important;
}

p {
	text-indent: 1.5em !important;
}
a:link, a:active, a:visited {
	color:#858585 !important;
        font:12px Segoe UI !important;
       
}
a:hover {
color:#fff !important;
        background:#d0d0d0 !important;
}
th,td {
	text-align: center !important;
}
table.vamid td {
	vertical-align: middle !important;
}
table.leftalign,table.leftalign td,table.leftalign th {
	text-align: left !important;
}
img {
	border-width: 0 !important;
}
acronym {
	cursor: help !important;
}
#wrapper {
	width: 900px !important;
	max-width: 800px !important;
	margin: auto !important;
	text-align: justify !important;
}
#top {
	background: url("http://i44.tinypic.com/2li7ja0.png") no-repeat !important;
	width: 825px !important;
	color: #000 !important;
	height: 160px !important;
}
#middle {
	background: url("http://i43.tinypic.com/fp7dqo.png") repeat-y !important;
	padding: 0 60px !important;
	color: #000 !important;
	min-height: 100px !important;
	width: 705px !important;

}
#bottom {
	background: url("http://i44.tinypic.com/118da9i.png") no-repeat !important;
	color: #000 !important;
	width: 825px !important;
	height: 110px !important;
}
#logo {
	display: block !important;
	margin: 0 auto !important;
	width: 562px !important;
	height: 127px !important;
	display:none !important;

}
#menu {
	text-align: center !important;
	margin: 0 auto !important;
}
.cave,.scroll,.login,.register,.shop,.forum,.logout {
	margin: 0 5px !important;
	background: transparent url("http://i42.tinypic.com/1fckyu.png") no-repeat !important;
	height: 50px !important;

}
.cave {
	width: 90px !important;
}
.scroll {
	width: 93px !important;
	background-position: -93px center !important;
}
.login {
	width: 90px !important;
	background-position: -190px center !important;
}
.register {
	width: 133px !important;
	background-position: -282px center !important;
}
.shop {
	width: 82px !important;
	background-position: -362px center !important;
	display:none !important;
}
.forum {
	width: 100px !important;
	background-position: -413px center !important;
}
.logout {
	width: 136px !important;
	background-position: -510px center !important;
}
#prefooter {
	margin-left: -11.5px !important;
	height: 100px !important;
	overflow: hidden !important;
	width: 728px !important;
	margin-right: -20px;
}
#footer {
	font-size: 0.8em !important;
	text-align: center !important;
	margin: 0 auto;
}
}
