/* ==UserStyle==
@name           yugenanime arck dark theme
@namespace      https://github.com/cybrejon/yugen-arc-dark-theme
@version        1.0.0
@description    arc dark theme for yugenanime
@author         cybrejon/TanYu
==/UserStyle== */

@-moz-document domain("yugenanime.ro") {
    
    body {
        background-color: #282B38;
    }
    
    header {
        background-color: #282B38;
        border: none;
    }
    
    .header--logo {
        content: "";
        width: 200px;
        height: 30px;
        background-position: center;
        background-image: url('https://cdn.glitch.global/a71f2c83-f4e1-4463-9cea-479191ee9342/yugen.png?v=1678192906940');
        background-size: contain;
        background-repeat: no-repeat;
    }
    
    .header--logo img {
        display: none;
    }
    
    .header--center {
        justify-content: center;
    }
    
    .header--center .search--container {
        width: 300px;
        margin: 0;
    }
    
    .header--center, .header--end {
        column-gap: 20px;
    }
    
    .main-input-box {
        background-color: #21232e;
        border: none;
        padding: 1px 0 1px 15px;
    }
    
    .main-input-submit {
         background-color: #21232e;
    }
    
    .header--button-svg svg {
        fill: #fffc;
    }
    
    aside.navigation {
        padding: 0 6px;
        border: none;
        background-color: #282B38;
    }
    
    aside.navigation .nav-item {
        font-size: 0;
        border-radius: 10px;
    }
    
    aside.navigation .nav-item svg {
        width: 29px;
    }

    
    .btn {
        background-color: #353845;
        padding: 10px 15px 9px 15px;
        font-weight: bold;
        border-radius: 5px;
    }
    
    .btn:hover {
        background-color: #4a4f61;
    }
    
    .btn:active {
        background-color: #1b1d25;
    }
    
    .btn:focus {
        outline: none;
        background-color: #2f323f;
        box-shadow: none;
    }
    
/*     language filter */
    body > main > div:nth-child(1) > section.m-25-t > div.flex.justify-between.m-15-b {
        margin-bottom: 40px;
    }
    
/*     log in button */
    #end > a {
        border: none;
    }
    
/*     editor's pick */
    body > main > section {
        background: transparent !important;
    }
    
    .ya-title {
        font-size: 3rem;
        margin-bottom: 15px;
    }
    
    .ya-title > svg {
        display: none;
    }
    
    .swiper {
      display: none;
    }
    
    .lozad, a.ep-thumbnail {
        border-radius: 10px;
    }
    
    .anime-poster__container {
        border-radius: 9px;
    }
    
    .review--card {
        border-radius: 10px;
        background-color: #353845;
    }
    
    .review--header {
        color: #749f7c;
    }
    
    body > main > div:nth-child(1) > section.m-25-t > div.flex.justify-between.m-15-b > div:nth-child(1) {
        display: flex;
        gap: 10px;
    }
    
    .sel-lang-middle, .sel-lang-right {
        border: none;
    }
    
    .plyr {
        border-radius: 10px;
        overflow: hidden;
    }
    
    .player--controls {
        background-color: transparent;
        box-shadow: none;
    }
    
    .box {
        background-color: #303441;
        box-shadow: none;
        border-radius: 10px;
    }
    
    .tag {
        background-color: #01CC8F;
        color: #282B38;
        font-weight: 800;
        border: none;
        border-radius: 50px;
        padding: 5px 15px 5px 15px;
    }
    
    .tag:hover {
        color: #01CC8F;
        background-color: #282B38;
    }

}
