@-moz-document domain("pony.town")
{
 /*first :>*/
  /*beginning*/
  body, html
  {
    margin: 0;
    padding: 0;
    background-image: url("https://cdna.artstation.com/p/assets/images/images/005/622/646/large/samuele-latino-snowdin-final-post-1080.jpg?1492532837");
    color: #82b3b3;
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,sans-serif;
    font-size: 14px;
  }

  .character-tab[_ngcontent-c14]
  {
    background: #1d2a3a;
    margin-top: 10px;
    padding: 10px 15px;
    min-height: 900px;
    border-radius: 2px;
  }

  .form-control:disabled, .form-control[readonly]
  {
    background-color: #1a262d;
    opacity: 1;
  }

  .form-control
  {
    display: block;
    width: 100%;
    height: calc(2.25rem + 2px);
    padding: .375rem .75rem;
    font-size: 1rem;
    line-height: 1.5;
    color: #eae2e2;
    background-color: #0a1919;
    background-clip: padding-box;
    border: 1px solid #d2b5b5;
    border-radius: .25rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
  }

  .nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active
  {
    color: #eee;
    background-color: #263c48;
    border-color: #191e3c;
  }

  .nav-pills .nav-link.active, .nav-pills .show>.nav-link
  {
    color: #fff;
    background-color: #161d3c;
  }

  .btn-default
  {
    color: #212529;
    background-color: #566e90;
    border-color: #544e4e;
  }

  .btn-success
  {
    color: #fff;
    background-color: #5c71b8;
    border-color: #5c5db8;
  }

  .btn-group>.btn-success:not(:first-child), .input-group-append>.btn-success:not(:first-child), .input-group-prepend>.btn-success:not(:first-child)
  {
    border-left-color: #dce6dc;
  }
 /*wait for version 2.0*/
