css:
/* i really want this to be global */

/*背景圖片替換成漸層色*/
body {
    background: linear-gradient(90deg, rgba(191, 169, 98, 1) 0%, rgba(191, 130, 121, 1) 100%);
}

#plurker_list .plurker .moreInfo {
    color: #999;
}

.aboutme.cboxAnchor {
    color: #DDD;
}

#plurker_list .plurker {
    background: #333;
}

#plurker_list .plurker:hover {
    background: #555;
    cursor: pointer;
}

#noti-list li.new-noti {
    background: #444;
}

#noti-list li.new-noti:hover {
    background: #666;
}

.poster_holder {
    border-top: 0px !important;
}

/*黑色名字改灰色*/
.name {
    color: #777;
}

.response_box {
    border-top: #eee 0px solid;
}

/*噗文黑底白字*/
.plurk_cnt {
    color: rgb(204, 204, 204) !important;
    background-color: #222 !important;
}

._lc_ .response_box {
    background-color: #222;
}

.plurk_box .mini_form {
    border-top: #222 1px solid;
    background-color: #222;
    font-weight: normal;
}

.plurkForm.mini-mode .textarea_holder textarea,
.mini_form .textarea_holder textarea {
    background-color: #444;
}

.input_holder>* {
    background-color: #444;
}

.input_holder {
    background-color: #444;
}

._lc_ .textarea_holder textarea {
    color: rgb(204, 204, 204);
}

.textarea_holder textarea {
    background: #444;
}

.response.show-response-manager .plurk_cnt {
    background: #333 !important;
}

.response-manager {
    background: #333;
}

/*噗文超連結顏色*/
.plurk a.ex_link {
    color: #348ec7;
}

/*外部連結顏色*/
.plurk a.ogvideo,
.plurk a.videoservices {
    color: #348ec7;
}

.plurk.porn-lock .need-check {
    background: #333;
}

/*展開的寬度*/
.timeline-cnt .display {
    width: 420px;
}

.m_qualifier i:before {
    color: #333;
}

.response_box::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    background-color: #333;
}

.response_box::-webkit-scrollbar {
    width: 10px;
    background-color: black;
}

.response_box::-webkit-scrollbar-thumb {
    background-color: #777;
}

.plurk-poll--result.show-voted .poll-candidate.voted {
    border-color: #ddd;
}

.poll-candidate {
    border: 2px solid #111;
}


.divplurk {
    background: #222;
    color: #ddd;
}

#vert_timeline .plurk:hover {
    background: #666;
}

#search-date {
    background: #333;
}

#front-content {
    background: none;
}

.response_box {
    background: #222;
}

.text_holder {
    color: #eee;
}

.divresponse.show-response-manager {
    background: #444;
}

.pop-window-view {
    background: #222;
}

.cbox_right .cbox_ads {
    border-top: 0px solid #DDDDDD;
}

.form-holder {
    background: #222;
}

.form-table {
    background: #333;
    color: #eee;
}

#settings-header {
    background: #222;
}

#settings-list li a {
    color: #999 !important;
}

#settings-list li a:before {
    color: #999;
}

input,
textarea,
select {
    border: 1px solid #999;
    background: #222;
    color: #eee;
}

.form-control input[type="text"],
.form-control input[type="password"],
.form-control input[type="radio"],
.form-control input[type="checkbox"],
.form-control select,
.form-control textarea,
.form-control .info-holder {
    color: #eee;
}

.pop-window-header {
    background: #333;
    border-bottom: #000 1px solid;
    color: #eee;
}

.theme-selector {
    background: #222;
}

.theme-selector:hover {
    background: #282828;
}

.pop-view {
    border: #222 1px solid;
    background: #333;
    color: #eee;
}

.theme-menu ul {
    border-bottom: #333 1px solid;
}

.theme-menu li {
    background: #333;
    color: #eee;
}

#creature-selection .creature.current {
    background: #555;
}

.pop-menu li a {
    color: #eee;
}

.pop-menu li a:not(.no-hover):hover {
    background: #555;
}

.more-options .option-limit {
    background: #444;
}

.more-options .selection {
    border: #222 1px solid;
    background: #444;
}

.more-options .checkbox {
    border: #222 1px solid;
    background: #444;
}

.poll-editor-popwindow .pop-window-header {
    color: #bbb;
    background: #222;
}

.poll-editor-container {
    background: #333;
}

.poll-editor-section-content {
    background: #444;
}

.poll-editor-section-title {
    color: #eee;
}

.poll-editor-settings-item-title {
    color: #eee;
}

.poll-editor-checkbox-label {
    background: #777;
}

.poll-candidate .candidate-background {
    background: #555;
}

.time-picker-msg {
    color: #eee;
}

.time-picker-input-container {
    background: #444;
    border: 1px solid #444;
}

.time-picker-select {
    color: #eee;
    border-width: 0px;
}

.time-picker-input {
    color: #eee;
}

.poll-editor-button-finish:disabled {
    background: #555;
}

#noti-list .pop-view-content {
    background: #222;
}

#noti-list ul {
    background: #333;
}

#noti-list li {
    color: #eee;
    border-bottom: #555 1px solid;
}

#noti-list .header {
    border-bottom: #555 1px solid;
}

#noti-list li a {
    color: #eee;
}

#noti-list li:hover {
    background: #444;
}

#pop-window-header {
    background: #222;
    border-bottom: #555 1px solid;
    color: #bbb;
}

#pop-window-tabs>ul {
    background: #222;
    border-bottom: #555 1px solid;
}

#permanent-plurk .plurk_box {
    background: #333;
}

#emoticons-holder {
    background: #333;
}

#emoticons-holder ul li:hover {
    background: #555;
}

#emoticon-tabs li.current {
    background: #555;
}

.plurk_editor_box .plurk {
    background: #222;
}

.checkbox--label {
    border: 2px solid #555;
}

.checkbox--background {
    box-shadow: inset 0 0 0 2px #555;
}

.checkbox .checkmark.checkmark-radio {
    background: #ccc;
}

.checkbox--control:checked~.checkbox--background .checkmark {
    opacity: 1;
}

.checkbox.radio .checkbox--control:checked~.checkbox--background {
    background-color: transparent;
    box-shadow: inset 0 0 0 2px #ccc;
}

.checkbox.checked~.checkbox--label {
    border-color: #ccc;
}

.bar-color {
    background: #9c583e;
}

#top_bar,
#top_bar .bar-icon,
#top_bar a {
    color: #ffffff;
}

.front-ad-banner {
    display: none;
}

#footer {
    color: #fff;
}

#footer a {
    color: #FFF;
}

/*時間軸的方塊顏色*/
.bottom_start {
    background: #995a3b;
}

/*時間軸的線的顏色*/
.bottom-line {
    background-color: #995a3c;
}

/*取消噗主的底線*/
.highlight_owner .name {
    text-decoration: none;
}

/*替換紫色*/
a.name[style="color:#AE00B0"] {
    color: #dc74dc !important;
}

/*替換綠色(自己)*/
a.name[style="color:#0a9c17"] {
    color: #62cf6b !important;
}

/*替換hashtag*/
.plurk_content a.hashtag,
.plurk .text_holder a.hashtag {
    color: #f79661;
}
