css:
@namespace url(http://www.w3.org/1999/xhtml);

/* style created by bam (http://www.cs-manager.com/csm/other/?p=other_info&s=manager&u=445853) */

/* credits to Tim aka "flexy" (http://www1.cs-manager.com/csm/other/?p=other_info&s=manager&u=228617) */
/* and Rafael "3" Cardoso (http://www1.cs-manager.com/csm/other/?p=other_info&s=manager&u=186744) */

/* this whole style is based on their awesome work... */

@-moz-document domain("cs-manager.com") {

#menu, #footer_left, #footer_right {
background-color: #232323 !important;
}

div#content_fixed table.dev tr td.value img[height="60"], div#content_fixed table.dev tr td.info img[height="60"] {
display: block !important;
margin-left: auto !important;
margin-right: auto !important;
}

#ads_container {
}

#clock{
font-weight: normal !important;
}

div.post {
background-color: transparent !important;
}

/* general font and background */

body {
background-color: #FFFFFF !important; 
background-image: url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/mainBG.png") !important;
background-repeat: repeat-x !important;
} 

.dev tr td , .inline tr td, .inline input{
}

div#header a[href="/"]{
visibility: hidden !important;
}

a:link, a:visited {
color: #606060 !important;
text-decoration: none  !important;
}

/*center all*/

#container, #footer_container{
border-left: 1px solid #232323 !important;
border-right: 1px solid #232323 !important;
}

#header{
border-left: 1px solid #605f5f !important;
border-right: 1px solid #605f5f !important;
}

#container, #footer_container, .post, #content, #header {
margin: auto !important;
}

/* header */

#header{
background-image: url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/title.png") !important;
background-color: #232323 !important;
}

/* clan, games/league, .... */

div#menu h3{
background-image: url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/menuBG.png") !important;
background-color :#4e84ed !important;
border-bottom-color: #326ee1 !important;
background-repeat: repeat-x !important;
}

div#info h3{
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/infoBG.png") !important;
background-color:#ec4f59 !important;
border-bottom-color: #d53944 !important;
background-repeat: repeat-x !important;
}

div#menu h3, div#info h3{
background-position: top right !important;
border-top:0 !important;
border-bottom-width: 1px !important;
color: #ffffff !important;
font-variant: small-caps !important;
padding-top: 4px !important;
padding-bottom: 4px !important;
}

/* presentation, news, players, ...*/

ul.menu li a, ul.gameinfo li a, ul#friend li a{
background-color: #ffffff !important;
color: #606060!important;
border-color: #232323 !important;
}

/* presentation, news, players, ... HOVER EFFECT */

ul.menu li a:hover{
background-color :#4e84ed !important;
color: #ffffff !important;
}

ul.gameinfo li a:hover, ul#friend li a:hover{
background-color :#ec4f59 !important;
color: #ffffff !important;
}

ul#friend li a:hover span{
background-color:#ec4f59 !important;
color: #ec4f59 !important;
}

/* buddy icon */

ul#friend a[title="Active"] span{
background-color: transparent !important;
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/online-4.png") !important;
background-repeat: no-repeat !important;
background-position: top right !important;
color: #ffffff !important;
padding-top: 0px !important;
padding-right: 15px !important;
}

ul#friend a[title^="Idle:"] span{
background-color: transparent !important;
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/idle-4.png") !important;
background-repeat: no-repeat !important;
background-position:  right top !important;
color: #ffffff !important;
padding-top: 0px !important;
padding-right: 15px !important;
}

/*title*/

div#content_fixed table.dev tr td.head, form tr td.head, div#content table.rank tr td.head, div#content table.login tr td.head, div#content_fixed table.data tr td.head, div#guestbook h2, h2, table.staff tr td.head, table.calender tr td.head, table.mail tr td.head {
background-image: url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/content.png") !important;
background-repeat: repeat-x !important;
background-position: top left !important;
background-color: #ffffff !important;
border-bottom: 0 !important;
border-top: 1px solid !important;
border-left: 1px solid !important;
border-right: 1px solid !important;
border-color: #dfdfdf !important;
color: #606060 !important;
}

/* forum/logout button */

div#header ul#tabtop li a{
border-color: #232323 !important;
background-color: transparent !important;
font-family: calibri !important;
font-weight: bold !important;
font-size: 8pt !important;
font-variant: small-caps !important;
}

div#header ul#tabtop li a[title="Forum"]{
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/forum.png") !important;
color: #4e84ed !important;
}

div#header ul#tabtop li a[title="CSM"]{
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/csm.png") !important;
color: #4e84ed !important;
}

div#header ul#tabtop li a[title="Log out"]{
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/logout.png") !important;
color: #ec4f59 !important;
}

/* middle content value & info + lineup*/

td.info, td.foot, table.lineup tr td.position, div#page h2, div[style="width: 602px;"] h3{
color: #232323 !important;
background-color: #ededed !important;
}

td.value, table.lineup tr td.player a, div.players ul#players li a, table.info tr td, tabel.dev tr td.tactic a.tactic{
color: #232323 !important;
background-color: #ffffff !important;
}

/*upcoming game*/

div#upcoming{
color: #232323 !important;
}

/* search*/

form#search{
background-color: #ffffff !important;
}

input[name="q"], select[name="s"]{
background-color: #ededed !important;
}

input.button, input.buttonsmall{
background-image: url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/content.png") !important;
background-repeat: repeat-x !important;
background-position: center center !important;
border-color: #606060 !important;
border-width: 1px !important;
color: #606060 !important;
font-weight: normal !important;
}

/*lancamp*/

.lan .info{
background-color: #ededed !important;
background-image:url("http://users.telenet.be/tim.daminet/csm/lancamp.png") !important;
background-repeat: no-repeat !important;
background-position: center right !important;
}

.lan .value{
background-color: #ededed !important;
}

/*mail & scribble & guestbook*/

table.mail tr.head td, div#guestbook h3, td.select, td.delete{
background-color: #ededed !important;
text-transform: uppercase !important;
}
div#guestbook div.post{
background-color: #ffffff !important;
}

table.mail tr td{ 
}

div#guestbook div.post div.modify{
background-color: #ffffff !important;
}

div#guestbook div.post div.modify > a:first-child{
color:green !important;
}

div#guestbook div.post div.modify a{
color: red !important;
}

/*tab help & tab vip*/

li.right a#help_tab{
background-color: #d9d9d9 !important;
}
li.vip a{
background-color: #ffdddd !important;
}

/********************************************* ICONS **********************************************/

/* no icons */
}

/********************************************* INFO **********************************************/

@-moz-document url("http://www.cs-manager.com/forum/") {
#info {
background-color: #ffffff !important;
}
}

@-moz-document url("http://www.cs-manager.com/csm/") {
#info {
background-color: #232323 !important;
}
}
/********************************************* 2D Prematch & Tactics **********************************************/

@-moz-document url-prefix("http://www.cs-manager.com/applet") {

body {
background-color: #ffffff !important; 
background-image: none !important; 
margin: Auto !important;
width: 900px !important;
}

body:after {
content: none  !important;
} 

h1 {
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/title.png") !important;
background-position: center left !important;
border-color: #000000 !important;
border-bottom: 0 !important;
border-top-width: 1px !important;
border-left-width: 1px !important;
border-right-width: 1px !important;
color: transparent !important;
height: 84px !important;
}

table tr td.head {
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/title.png") !important;
background-position: center left !important;
border-color: #000000 !important;
border-bottom-width: 1px !important;
border-top-width: 1px !important;
border-left-width: 1px !important;
border-right-width: 1px !important;
color: transparent !important;
height: 90px !important;
}

table tr td {
background-color: #ededed !important
}

table tr td#link {
background-color: #232323 !important;
border-top: 1px #000000 solid !important;
color: #ededed !important;
}

table tr td#link a, table tr td#link div {
position: relative !important;
top: 30px !important;
}

table tr td[style="border-left: 1px solid #000000;background: rgb(240, 243, 249) none repeat scroll 0% 0%;height: 500px;-moz-background-clip: border;-moz-background-origin: padding;-moz-background-inline-policy: continuous;"], table tr td[style="background: rgb(240, 243, 249) none repeat scroll 0% 0%;height: 440px;-moz-background-clip: border;-moz-background-origin: padding;-moz-background-inline-policy: continuous;padding-top: 60px;"],table tr td[style="border-right: 1px solid #000000;background: rgb(240, 243, 249) none repeat scroll 0% 0%;height: 500px;-moz-background-clip: border;-moz-background-origin: padding;-moz-background-inline-policy: continuous;"] {border-top: 1px #000000 solid !important;}

table tr td table.players {
border: 0px !important;
position: relative !important;
top: -30px !important;
left: 40px !important;
}
}


/********************************************* 2D Live **********************************************/

@-moz-document domain("applet.cs-manager.com") {

body {
background-color: #ffffff !important;
background-image: none !important; 
}

h1 {
background-image:url("http://i600.photobucket.com/albums/tt87/ryudoh/CSM%20Promod%20Theme/title.png") !important;
background-position: center left !important;
border-color: #000000 !important;
border-bottom: 0 !important;
border-top-width: 1px !important;
border-left-width: 1px !important;
border-right-width: 1px !important;
color: transparent !important;
height: 84px !important;
}
}
