css:
@-moz-document domain(douban.com) {
.ui-slide-control .btn-prev {
    box-sizing: border-box;
    position: relative;
    display: block;
    transform: scale(var(--ggs, 1));
    width: 18px;
    height: 18px;
    border: 2px solid;
    border-radius: 100px;
}
.gallery-ui-slide-control .gallery-btn-prev {
        box-sizing: border-box;
        position: relative;
        display: block;
        transform: scale(var(--ggs, 1));
        width: 18px;
        height: 18px;
        border: 2px solid;
        border-radius: 100px;
}


.ui-slide-control .btn-prev::after {
    content: "";
    display: block;
    box-sizing: border-box;
    position: absolute;
    width: 4.9px;
    height: 4.9px;
    border-bottom: 2px solid;
    border-left: 2px solid;
    transform: rotate(45deg);
    left: 5px;
    top: 4.9px
}

.ui-slide-control .btn-prev:hover:after {
    color: white;
}

.gallery-ui-slide-control .gallery-btn-prev::after {
    content: "";
    display: block;
    box-sizing: border-box;
    position: absolute;
    width: 4.9px;
    height: 4.9px;
    border-bottom: 2px solid;
    border-left: 2px solid;
    transform: rotate(45deg);
    left: 5px;
    top: 4.9px
}

.gallery-ui-slide-control .gallery-btn-prev:hover:after {
    color: white;
}

.ui-slide-control a {
    color: #6ea2d9;
    background: none !important;
}
.gallery-ui-slide-control a {
    color: #6ea2d9;
    background: none !important;
}

.ui-slide-control a:hover {
    color: #6ea2d9;
    background: #6ea2d9 !important;
}
.gallery-ui-slide-control a:hover {
    color: #6ea2d9;
    background: #6ea2d9 !important;
}

.ui-slide-control .btn-next {
    box-sizing: border-box;
    position: relative;
    display: block;
    transform: scale(var(--ggs, 1));
    width: 18px;
    height: 18px;
    border: 2px solid;
    border-radius: 100px
}
.gallery-ui-slide-control .gallery-btn-next {
    box-sizing: border-box;
        position: relative;
