css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("elcomercio.pe") {



.media .title{ min-height: 30px !important; padding:6px !important; width:300px !important;}
.media h3{ font-size:11px  !important; font-family:tahoma !important; line-height:15px !important;}

#header-all{padding:18px 0 0 0 !important;}

.comerciope{ margin:0px auto 17px !important;}

#ad_banner{ display:none !important;}

ul#tabs-mercados li a.activo, ul#tabs-mas li a.activo{ background-color:#fff !important; background-image:none !important;  -moz-border-radius:5px; }

ul#tabs-mercados li a.activo span, ul#tabs-mas li a.activo span{ background-image:none !important }


#ult-momento-menu{ background: #dadada !important; border-top: solid 1px #ccc !important; border-bottom: solid 1px #ccc !important;  }
#ult-momento-menu li a span, #ult-momento-menu li a{ background:none !important; }
#ult-momento-menu .activo {background:#fff !important; -moz-border-radius:5px;}

.box-note h2 { margin-left:0 !important; margin-right:0 !important;}
.box-note p.intro { padding-left:0 !important; padding-left:0 !important;}
.boton{ display:none;}
ul#lista-tags li a{ text-transform:none !important;}
ul#destacados-menu li a:hover{ text-decoration:none !important; }
ul#destacados-menu li a:hover span{color: #efefef !important;}

a{ color: #148CB5 !important; }

.ads-615x60, .ads-960x90, .cnt-banner-p, .impunidad, .suscripciones-b, .sudoku, .ads-300x250, .share, .time, .ads-300x800, .accesos, #comunidad-bar, .img-autor, .ver-mas, .fechacommnet  { display:none !important;}

.nota-contenido{ width : 630px !important;}
.all-news .box-note h2{ font-size: 14px !important; font-weight:bold !important;}
#compartir{ display:none;}

body#nota h1{ color:#000 !important;  font-size:28px !important;}


#commentable{  width : 630px  !important;}
#comentarios{ width : 630px  !important;}

.total-like, .total-commentarios, .content-comentario { background:none !important;}
.detalle-comentario{ font-size:12px !important; color:#333 !important; }
.content-comentario{font-family:Arial;  line-height:16px !important;  width:610px !important; padding:5px 10px !important;  }

.detalle-comentario{ padding:0 !important; }

#content-footer{ height:1px;}
.autor-coonmentario .hide{ display: inline !important; }
}
