css:
@-moz-document url(https://habitica.com/) {
	footer {
  display: none!important;
}

#app-header {
  background: #212121!important;
  padding-top: 25px!important
}

.navbar {
  background: #171717!important;
  padding: 1rem 1.5rem!important;
}

.topbar-item>a[data-v-0b0a2436] {
  padding: .5em 1em!important;
}

.navbar-brand {
  display: none!important;
}

.tasks-navigation {
  display: none!important
}

h2 {
  font-family: poppins!important;
  font-size: 16px!important;
  font-weight: 600!important;
}

.topbar-dropdown-item {
   font-family: poppins!important;
  font-size: 13px!important;
  font-weight: 400!important;
  padding: .4em 1em!important;
  background: #3c3c3c!important;
}

.topbar-dropdown-item:hover {
  background: #272727!important;
}

.nav-link {
  font-family: poppins!important;
  font-size: 13px!important;
  font-weight: 400!important;
  padding: .4em 1em!important;
}

.topbar .topbar-item.active[data-v-0b0a2436]:not(:hover) {
  box-shadow: inset 0 -4px 0 #3c3c3c!important;
}

.topbar .topbar-item:hover {
  background: #3c3c3c!important;
}

.topbar .topbar-item {
  padding-top: unset!important;
  height: unset!important;
}

.top-menu-icon.svg-icon {
  width: 16px!important;
}

.avatar {
  border-radius: 6px!important;
}
}
