css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("desk.zoho.com")

{.zd-topbar-container {
  background-color: #000 !important;
}
/* sidebar on the left */
.zd-homelayout-leftPanel, .zd-menucollapse-mInfo {
    background-color: #000 !important;
}
/* buttons in the popup */
.zd-rippleeffect-primary, .zd-rippleeffect-active {
  height: 50px;
  width: 50px;
}

/* sidebar back button in the pop up*/
.zd-rippleeffect-default, .zd-rippleeffect-defaultHover {
/*  z-index: -100; */
  background-color: white;
  height: 50px; 
  width: 50px; 
}
/* exclude the dropdown view selector*/
.zd-viewspopup-btn {
  height: 50px;
  width: 25%;
}
/* ticket options panel*/
.zd-desktab-container{
  width: 50px; 
  }
/* ticket options panel*/
.zd-detailviewlayoutnew-backContainer {
  width: 100px;
}
/* side panel */
.zd-tab-textContainer {
  width: 50px; 
}
/* exclude the top nav menu items */
.zd-tab-tab {
  width: 90px}
}
