@-moz-document url-prefix('https://www.youtube.com/') {
  /* Define the animation */
  @keyframes glow {
    0% {
      box-shadow: 0 0 0 10px rgba(0, 123, 255, 0.4);
    }
    25% {
      box-shadow: 0 0 0 20px rgba(0, 123, 255, 0.8);
    }
    50% {
      box-shadow: 0 0 0 0px rgba(0, 0, 0, 0.0);
    }
    55% {
      box-shadow: 0 0 0 -10px rgba(0, 0, 0, 0.0);
    }
    75% {
      box-shadow: 0 0 0 -20px rgba(0, 123, 255, 0.8);
    }
    100% {
      box-shadow: 0 0 0 10px rgba(0, 33, 87, 0);
    }
  }

  /* Apply the animation to all YouTube thumbnails */
  #thumbnail:hover,
  .yt-simple-endpoint.inline-block.style-scope.ytd-thumbnail:hover {
    animation: glow 1s ease-in-out infinite;
    border-radius: 5px;
    z-index: 9999;
    transform: scale(1.1);
  }

  /* Decrease z-index of video preview */
  #mouseover-overlay {
    z-index: 1;
  }

  /* Show progress bar */
  .ytp-progress-bar-container {
    opacity: 1 !important;
    pointer-events: all !important;
  }

  /* Add transition effect when clicking on video */
  .html5-main-video {
    transition: all 0.5s ease-in-out;
  }

  /* Scale video up when clicked */
  .html5-main-video:active {
    transform: scale(1.2);
  }
}
