css:
/* i really want this to be global */

:root {
    --theme: #67e2f5;
    --bg: url('https://pbs.twimg.com/media/Fp28UcjaIAAgag_?format=jpg&name=large');
    --bg-op: 0.45;
    --tweet-op: 0.00;                                            
    --lettercase: unset;
}

/* Hide the Twitter bird */
[aria-label='Twitter'] [aria-hidden="true"] {
    display: none !important;
}

/* Add the new logo as a background image */
[aria-label='Twitter'] {
    display: block !important;
    width: 40px;
    height: 40px;
    background-image: url(https://pbs.twimg.com/profile_images/1613536963830620161/sVlo567Q_400x400.jpg);
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center center;
}

/* Replace the text "Home (New unread Tweets)" with "Ether" */
a[data-testid="AppTabBar_Home_Link"][aria-label="Home (New unread Tweets)"] span[dir="auto"]::before {
  content: "Ether";
  font-weight: bold;
}

html {
    background: var(--bg) fixed !important;
    background-size: cover !important;
}

/*darken bg*/
.r-150rngu > div:nth-child(1):not(.r-1d0k16c):not(.r-kemksi):not(.r-1ye8kvj):not(.r-ouzzow):not(.r-136ojw6) {
    /* - DM bg, DM bg, DM bg, DM bg pop-out DM */
	background: rgba(0,0,0,var(--bg-op)) !important;
	outline: solid rgba(0,0,0,var(--bg-op)) 9999px !important;
}

/* remove original bg */
body {
    background: transparent !important;
}
