css:
@-moz-document domain("onche.org") {
/*Version 1.0.2
Par DELAWARDE */
#content,
html {
    background-image: url('https://i.imgur.com/ylMMFPc.jpg')!important;
    background-size: cover!important;
    background-attachment: fixed!important;
    position: relative;
}



.bloc,
.message,
#menu {
    background-color: #24001cf0!important;
    color: #f3cbff;
    outline: none;
    box-shadow: 0 0 10px #4cc8ff!important;
    text-shadow: 1px 1px 2px #3daaff;
    transition: background-color 1s, text-shadow 1s, box-shadow 1s;
}
.topic:nth-child(2n + 1) {
    background-color: #24001c69!important;
}
.message:hover {
    background-color: #200240bd!important;
    text-shadow: 0 0 10px #3daaff;
    box-shadow: 0 0 50px #3daaff!important;
}

.message-content {
    font-size: 16px!important;
}

.topic-subject,
.content.links {
    color: #ffcffd!important;
    font-size: 15px!important;
}

.content.topics {
    padding: 0px #000000!important;
}

.topic-subject:hover,
.topic-left,
.right {
    background-color: #24001c;
    border-top: solid 0px!important;
}




a {
    color: #00f2ff!important;
    font-size: 15px!important;
}

.profile-cover-username {
    color: #00f2ff!important;
}

.message-username {
    font-size: 18px!important;
}

.topic {
    padding: 0px!important;
    border-top: 0px!important;
}

.message-date,
textarea,
.preview,
.sticky-container,
.title,
input,
.pagination,
a.active {
    background-color: #200019f0!important;
    color: #ece1ff!important;
    text-shadow: 1px 1px 2px pink;
}

header {
    background-color: #200019f0!important;
}

._format._spoiler:not(.active) > div {
    background-color: #00f2ff!important;
    color: #00f2ff!important;
    text-shadow: none!important;
}

.container {
    min-width: 1400px!important;
}

.message.answer {
    box-shadow: none!important;
}

.see-more {
    background: linear-gradient(to bottom, rgba(213, 0, 192, 0) 0%, #51005cf0 65%)!important;
}
}
