css:
@-moz-document domain("youtube.com") {
    /* Background color */
    #page-manager {
        background-color: #FFB6C1;
    }

    /* Header */
    #masthead-container {
        background-color: #fff;
        height: 160px;
        margin-bottom: 20px;
    }

    /* Logo */
    #logo {
        background-color: #FF69B4;
        height: 95px;
        width: 150px;
        margin: 20px auto 0;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    /* Search bar */
    #search-input {
        background-color: #fff;
        border-radius: 25px;
        padding: 10px 20px;
        color: #000;
    }

    /* Navigation bar */
    #guide {
        background-color: #FF69B4;
    }

    /* Links in navigation bar */
    #guide .ytd-guide-entry-renderer a {
        color: #fff !important;
    }

    /* Footer */
    #footer-container {
        background-color: #FFB6C1;
        color: #fff;
    }

    /* Text in footer */
    #footer-container a {
        color: #fff;
    }
}
