css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("osiris.utwente.nl")
{
    form>table table:first-of-type tr[align=left] table:first-of-type
    {
        width: 100%;
    }

    form>table table:first-of-type tr[align=left] table:first-of-type td[valign="bottom"]
    {
        width: 100px;
    }

    form>table table:first-of-type tr[align=left] table:first-of-type td
    {
        width: 2px !important;
    }

    form>table table:first-of-type tr[align=left] table:first-of-type td:last-child
    {
        width: 100% !important;
        text-align: right;
    }

    .x5u .psbLink,
    .x5u .x5r,
    .x5u .x5s,
    .x5u .psbButtonLink,
    .x5u a[accesskey="P"]:hover,
    .x5u a[accesskey="H"]:hover,
    table[class=""] .psbLink,
    table[class=""] .x5r,
    table[class=""] .x5s,
    table[class=""] .psbButtonLink,
    table[class=""] a[accesskey="P"]:hover,
    table[class=""] a[accesskey="H"]:hover,
    #keuzeblok .x5r,
    #OpleidingExamenfaseStudent .x5s,
    .x11.x4x x5s
    {
         background-color: black;
         color: white !important;
         padding: 4px 20px !important;
    }

    #keuzeblok .x5s
    {
        color: black !important;
        padding: 2px;
    }

    #keuzeblok .x5s:before
    {
        content: "â–º ";
        text-decoration: none !important;
    }

    .x5u a[accesskey="P"],
    table[class=""] a[accesskey="P"],
    .x5u a[accesskey="H"],
    table[class=""] a[accesskey="H"]
    {
        background-color: #999;
    }

    .x1c td
    {
        padding: 4px !important;
    }

    .x1c img
    {
        display: none;
    }
  
    .x1c a
    {
        padding: 8px
    }

    .SelectedButton
    {
        cursor: pointer;
    }

    .x5n
    {
        border-bottom: none !important;
        padding-bottom: 10px;
        padding-top: 10px;
    }

    .x5u
    {
        border-top: none !important;
    }

    .x5n img
    {
        height: 1px !important;
    }

    .x5n td[valign=top]
    {
        padding-top: 10px;
    }

    .x6w
    {
        display: block;
        height: 24px;
        margin-top: 10px;
    }

    .x6g td
    {
        height: 32px;
        vertical-align: middle;
    }

    .psbVoetNoot
    {
        display: none;
    }

    img[src^='servlet/pasfoto?studentnummer=']
    {
        display: none;
    }
}
