css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.hardware.fr")  {
/*#############################################################

   Theme original DARK PAPER pour le forum HFR par Skopos

        version 1.1 BETA 

        support sur http://forum.hardware.fr/hfr/Graphisme/Webdesign/webdesign-themes-forum-sujet_32431_1.htm

##################################################################*/

/* Reset */

* {border: none !important;}
a:hover {text-decoration: none !important; }
br {display: none ;}


/* BR */
table.main br,
form .s2Ext br,
.messCase2 br,
.toberead br,
body.reponse br,
tr.message td br {display: inline ; }


/* BORDURES */
.cBackHeader td, .sujet td 
{
border:1px solid #333 !important;
border-right: 0px !important;
} 
.cBackHeader th + th {border-left: 1px solid #555 !important;}
.fondForum2Fonctions th {border-top: 1px solid #555 !important; }
.fondForum2Title th 
{
border-bottom: 1px solid #555 !important;
padding-right: 12px !important;
}
.fondForum1Subcat td {border-top: 1px solid #555 !important;}
.fondForumDescription th {border-top: 1px solid #555 !important;}

.message td.messCase1 {border-right: 1px solid #222 !important;}
.message td.messCase2 .toolbar {border-bottom: 1px solid #888 !important;}
table.main, table.messagetable {border-left: 1px solid #555 !important;}
table.main, table.messagetable {border-right: 1px solid #555 !important;}


/* image de fond, bordure, arrondi */

#mesdiscussions 
{
width: 98% !important;
max-width: 1024px;
position: relative; top: 20px;
}

/* FOND DU FORUM */
body 
{
color: #888 !important;
margin: 0 0 10px 0;
background-color: #bbb !important;
background-image:url("http://sifr.free.fr/Gfx/Stylish_img/tilefondforum.jpg");
position: relative !important;
}


/* MENU LOGO */
td.tdmenu {display: none; }
table.hfrheadmenu
{ 
width: 100% !important;
position: absolute;
top: -72px !important;
}
table.hfrheadmenu *
{
color: #333 !important;
background: #555 url("http://sifr.free.fr/Gfx/Stylish_img/blackgif.gif") repeat !important;
}
table.hfrheadmenu a:hover 
{
text-decoration: none !important;
color: #ea0 !important;
}

.fastsearchInput 
{
color: #4cf !important;
background: #555 url("http://sifr.free.fr/Gfx/Stylish_img/lightgif.gif") repeat !important; 
width: 6em !important;}

div[style="width: 99%;"] {width: 100% !important;}
span.menuExt 
{ 
background-image: url("http://sifr.free.fr/Gfx/Stylish_img/logo_cat12.jpg"), url("http://sifr.free.fr/Gfx/Stylish_img/fondheader.gif");
background-repeat: no-repeat, repeat-x;
background-position: 50% !important;
display: block;
height: 180px !important;
position: relative;
top: 25px;
border-bottom: 10px solid #222 !important;
}

/* MENU MODO */
.headerModeration 
{
height: 50px !important;
width: 50px !important;
position: absolute;
top: -100px;
right: 0px;
}


/* MENU ARBORESCENT */
.arbo *
{
color: #4cf !important;
display: inline !important;
}
.arbo img,
.arbo br {display:none !important;}
.arbo [id$="2"]:before {content:" ▐"; font-size: 12px;}
.arbo [id$="3"]:before, .arbo [id$="4"]:before {content:" ►";}
.arbo span[id$="1"] a{color: #777 !important;}
.arbo {height: 50px;}
.arbo a:hover 
{
color: #222 !important;
background-color: #ea0 !important;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
-o-border-radius: 3px;
border-radius: 3px;
}
.left + .arbo {margin: 10px 0 10px 20px !important;}



/* MENU CADRE ROUGE / LIEN REGLEMENT */
.menu {display: none;}


/* BOUTONS SUJETS */
div.rightbutton
{
position: relative;
top: 60px;
}
div.rightbutton a img,
div.nombres a img,
a img[alt="Ajouter une réponse"] {display: none;}
div.rightbutton a:before,
div.nombres a:before 
{
font-size: 14px;
color: #bbb;
padding: 3px 0.5em;
background-color: #222;
-moz-border-radius: 12px 3px 0 0;
-webkit-border-radius: 12px 3px 0 0;
-o-border-radius: 12px 3px 0 0;
border-radius: 12px 3px 0 0;
border: 1px solid #555;
content:"nouveau sondage";
}
div.rightbutton a[href$="sujet.htm"]:before,
div.nombres a[href$="sujet.htm"]:before,
div.rightbutton a[href*="sond=0"]:before,
div.nombres a[href*="sond=0"]:before
{
content:"nouveau topic";
}
div.rightbutton a[href*="cat=prive"]:before,
div.nombres a[href*="cat=prive"]:before
{
content: "nouveau MP";
}

/* répondre (page topic) */

a[accesskey="r"]:after
{
content: "répondre";
font-size: 14px;
color: #bbb;
padding: 3px 0.5em;
background-color: #222;
-moz-border-radius: 0 0 3px 12px;
-webkit-border-radius: 0 0 3px 12px;
-o-border-radius: 0 0 3px 12px;
border-radius: 0 0 3px 12px;
border: 1px solid #555;
border-top: none !important;
position: relative;
top:2px;
}

/* boutons du bas */
div.nombres a:before
{
-moz-border-radius: 0 0 3px 12px;
-webkit-border-radius: 0 0 3px 12px;
-o-border-radius: 0 0 3px 12px;
border-radius: 0 0 3px 12px;
content:"nouveau sondage";
border: 1px solid #555;
border-top: none !important;
}
a[href*="carte"]:before  {content: inherit !important;}

.nombres b {color: #888;}
.nombres input {margin-right: 2em;}


/* ONGLETS FILTRES */
td[style="vertical-align: bottom;"]
{
float: left;
position:relative;
top:1px;
}
td[style="vertical-align: bottom;"] .left {margin-left: 2em;}

[id*="befor"],
[id*="after"] {display: none;
}
.onglet,
.ongletsel,
.ongletonmouseover
{
margin-right: 8px;
background-image: inherit !important;
background-color: #222 !important;
-moz-border-radius: 3px 12px 0 0 !important;
-webkit-border-radius: 3px 12px 0 0 !important;
-o-border-radius: 3px 12px 0 0 !important;
border-radius: 3px 12px 0 0 !important;
border: 1px solid #555 !important;
border-bottom: none !important;
}
.ongletsel
{
-moz-box-shadow: 0px -1px 10px #ea0;
-webkit-box-shadow: 0px -1px 10px #ea0;
-o-box-shadow: 0px -1px 10px #ea0;
box-shadow: 0px -1px 10px #ea0;
border-color: #ea0 !important;
}
.ongletonmouseover {background-color: #ea0 !important; }

/* MP */
.left .left img+a {color: #777 !important; padding-right:200px;}
.left .left img+a.red {color: #ea0 !important;}

/* ########### LISTE SUJETS ######################*/
/* sujet a lire */
.toberead,
.toberead *{color: #666 !important;
}
.toberead a:hover {
font-weight:bold;
color: #222 !important;
background-color: #ea0;
}

/* SOUS-CAT */
tr.fondForum1Subcat td {font-size: 0;
}
tr.fondForum1Subcat td a {
font-size:16px;
margin: 8px ;
line-height: 26px;
padding: 0px 0.5em ;
display: inline-block !important;
color: #666 !important;
background-color: #333;
border: 1px solid #444  !important;
-moz-border-radius: 7px;
-webkit-border-radius: 7px;
-o-border-radius: 7px;
border-radius: 7px;
}

tr.fondForum1Subcat td b 
{ 
padding: 0 5px;
display: inline-block;
line-height: 26px !important;
font-size: 16px !important;
position:relative;   
border-width : 1px 0 !important;
-moz-border-image : url("http://sifr.free.fr/Gfx/Stylish_img/buttonborderorange.png") 5%  stretch;
-webkit-border-image : url("http://sifr.free.fr/Gfx/Stylish_img/buttonborderorange.png") 5%  stretch;
-o-border-image : url("http://sifr.free.fr/Gfx/Stylish_img/buttonborderorange.png") 5%  stretch;
border-image : url("http://sifr.free.fr/Gfx/Stylish_img/buttonborderorange.png") 5%  stretch;
color: #ea0 !important;
}


tr.fondForum1Subcat td a:hover {border-color: #ea0 !important;}
tr.fondForum1Subcat td b:hover {color: #222 !important;}

/* 
tr.fondForum1Subcat td {padding: 0; font-size: 0px; }
tr.fondForum1Subcat td { background: url("file:///E:/Utilisateurs/Skopos/Images/SetIconHFR/fondbutton.gif") repeat  !important;}


tr.fondForum1Subcat td a
{
font-weight: bold;
font-size: 16px !important;
line-height: 30px;
position:relative;
top:-4px;
padding: 0px 0.2em ;
display: inline-block !important;
}

tr.fondForum1Subcat td a:before
{
content:": ";
color: rgba(255,255,255,0.5);
}
tr[class*=fondForum1Subcat] td a:after
{
content:" :";
color: rgba(255,255,255,0.5);
}


tr.fondForum1Subcat a:hover 
{
text-decoration: none !important;
background-color: #4cf; }
*/

/* Pagination haut/bas des pages liste_sujets et messages */

.cBackHeader b+b
{
padding: 0 2px;
-moz-border-radius: 3px; color: #000;
-o-border-radius: 3px; color: #000;
-webkit-border-radius: 3px; color: #000;
border-radius: 3px; color: #000;
background-color: #4cf;
}
.cBackHeader span+b,
.cBackHeader a+b
{
padding: 0 2px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
-o-border-radius: 3px;
border-radius: 3px;
color: #000;
background-color: #4cf;
}
a.cHeader:hover
{
color: #000 !important;
background-color: #ea0;
}

.pagepresuiv {margin: 0 1em 0 0 !important;}
.cFondu {color: #555 !important;}

[class*="sujetCase"]
{background-color: #666 !important; color: #eee;}
[class*="sujetCase"] a 
{color: #ccc !important;}
tr[class$="en0"]> td.sujetCase3 a
{
color: #fff !important;
text-shadow: 0px 0px 6px #ff0;
}

tr[class$="cky"]> td[class^="sujetCase"]  
{background-color: #444 !important;}

/* cases 1 6 9 */
td.sujetCase1,
td.sujetCase6,
td.sujetCase9 
{background-color: #444 !important;}
td.sujetCase2 img
{
-webkit-opacity: 0.3;
-o-opacity: 0.3;
opacity: 0.3;
}
td.sujetCase2 img:hover
{
-webkit-opacity: 1;
-o-opacity: 1;
opacity: 1;
}

/* Sujets relatifs */
table#sujetrelatif tr.s2
{
background-color: #333 !important;
}
table#sujetrelatif tr.s2 a
{font-size: 0.8em;color: #777 !important;padding-left: 1em;}
table#sujetrelatif tr.s2 td {text-align: center !important;}
table#sujetrelatif tr.s2 a:hover, table#sujetrelatif tr.s1 a:hover {color: #222 !important; background-color: #ea0; border-radius: 3px;}
table#sujetrelatif tr.s1 {background-color: #222 !important;}
table#sujetrelatif tr.s1 a {color:#4cf !important;}

/* FOOTER */
.legende,
.toberead
{
margin : 2em 0;
padding: 1em;
background-color: #333;
}
.legende br {display: block;}

.copyright,
center
{
margin : 3em 0;
padding: 1em;
color: #888 !important;
background-color: #333;
}
center a {color: #08b !important;}
center {display: none;}



/* ################ MESSAGES ################*/

.main .left {margin-left: 10px;}

tr.message {background-color : transparent !important;}
.main tbody {background-color:#222 !important;}
.messagetable tbody {background-color:#222 !important;}
.messagetable
{
background-color:#222 !important;
padding: 0.5em !important;
}

td.messCase1 {
-moz-border-radius: 7px 0 0 7px !important;
-webkit-border-radius: 7px 0 0 7px !important;
-o-border-radius: 7px 0 0 7px !important;
border-radius: 7px 0 0 7px !important;
}
td.messCase2 {
-moz-border-radius: 0 7px 7px 0 !important;
-webkit-border-radius: 0 7px 7px 0 !important;
-o-border-radius: 0 7px 7px 0 !important;
border-radius: 0 7px 7px 0 !important;
}


.messagetable:nth-child(even) tr td {
color:#ccc !important;
background-color: #555 !important;
}
.messagetable:nth-child(even) table.citation,
.messagetable:nth-child(even) table.quote
{
background-color: #4d4d4d !important;
-moz-border-radius: 7px;
-webkit-border-radius: 7px;
-o-border-radius: 7px;
border-radius: 7px;
}
.messagetable:nth-child(even) table.citation td,
.messagetable:nth-child(even) table.quote td 
{
background-color: #4d4d4d !important;
color: #aaa !important;
}

.messagetable:nth-child(odd) tr td 
{
color:#eee !important;
background-color: #777 !important;
}
.messagetable:nth-child(odd) table.citation,
.messagetable:nth-child(odd) table.quote
{
background-color: #6b6b6b !important;
-moz-border-radius: 7px;
-webkit-border-radius: 7px;
-o-border-radius: 7px;
border-radius: 7px;
}
.messagetable:nth-child(odd) table.citation td,
.messagetable:nth-child(odd) table.quote td
{
background-color: #6b6b6b !important;
color: #ccc !important;
}

.messagetable:nth-child(odd) table.citation td{color: #222 !important;}
.messagetable:nth-child(even) table.citation td{color: #222 !important;}

.messagetable:nth-child(odd) a.cLink {color: #4cf !important;}
.messagetable:nth-child(even) a.cLink {color: #2ad !important;}

/* spoiler, code, fixe */
.spoiler,
.code,
.fixed
{
border: 1px solid #888 !important;
background-color: transparent !important;
}
.messagetable:nth-child(even) .spoiler,
.messagetable:nth-child(even) .code,
.messagetable:nth-child(even) .fixed
{
border: 1px solid #666 !important;
}


/* message Auteur */
td.messCase1 {text-align: center;}


.cBackHeader 
{
background-image : inherit !important ;
background-color: #222 !important;
}
.cBackHeader td
{
border-bottom : 1px solid #555 !important;
}
.fondForum2PagesBas td
{
border-left: 1px solid #555 !important;
border-right: 1px solid #555 !important;
}

/* LIENS */


/* IMAGES */
.messCase2 div p img:not([src*="http://forum-images.hardware.fr"])
{
-moz-box-shadow: 0 0 15px #111 ; 
-webkit-box-shadow: 0 0 15px #111 ;
-o-box-shadow: 0 0 15px #111 ;
box-shadow: 0 0 15px #111 ;
}

/* MODO ACTION */
input[value="Effacer/restaurer les messages sélectionnés"]
{
position: absolute;
bottom: 230px;
right:0; }

/* FOOTER MESSAGE */
.s2Ext b,
.s1Ext *
{color: #888 !important;}

/* ################ REPONSE RAPIDE ################### */
div.s2Ext {
position: fixed;
bottom: 20px;
left: 99.5%;
background-color: #111;
border: 7px solid #ea0 !important;
-moz-border-radius: 7px 0 0 7px;
-webkit-border-radius: 7px 0 0 7px;
-o-border-radius: 7px 0 0 7px;
border-radius: 7px 0 0 7px;
}
div.s2Ext:hover 
{
left: inherit;
right: 0px;
}

/* ################ PAGE ACCUEIL ####################*/
tr.cat:nth-child(odd) td{background-color: #555 !important; border: 1px solid #222!important;}
tr.cat:nth-child(even) td {background-color: #555 !important; border: 1px solid #222!important;}
tr.cat {color: #ccc !important;}
tr.cat a {color: #bbb !important;}

tr.cat td.catCase1{height: 50px;}
tr.cat td b {display: inline-block; margin: 5px 0!important;}
tr.cat td a.cCatTopic {color: #fff !important; font-size: 16px !important; padding: 0 5px;}
tr.cat td a:hover {color: #ea0 !important;}
tr.cat td a.cCatTopic:hover
{
color: #222 !important;
-moz-border-radius: 7px;
-webkit-border-radius: 7px;
-o-border-radius: 7px;
border-radius: 7px;
background-color: #ea0;
}

/* ################   PAGE REPONDRE  ################*/
#mesdiscussions table:nth-child(2n+18)  {background-color: #777;}
#mesdiscussions table:nth-child(2n+19)  {background-color: #555;} 


body.reponse {color: #fff !important; background-image: none !important;  background-color: #666 !important;}
}


@-moz-document url-prefix("http://forum.hardware.fr/message.php") {
.citation td{background-color: #555 !important;}

}
