css:
@-moz-document domain("onche.org") {
/*Thême MATRIX'ONCHE
Par DELAWARDE */
/*Style général*/
#content,
html {
    background-color: black!important;
    font-family: monospace!important;
    background-image: url('https://i.imgur.com/9vLgy9s.gif')!important;
    background-size: cover!important;
    background-attachment: fixed!important;
    position: relative;
}

a,
.item,
.item.onche {
    font-family: monospace!important;
    color: #00ff1a!important;
    font-size: 15px!important;
    text-shadow: 1px 1px 2px #004a08!important;
}

.button.filled {
    background-color: #00ff1a!important;
    color: black!important;
}

.message-date,
textarea,
.preview,
.sticky-container,
.title,
input,
.pagination,
a.active,
.favoriteStickers {
    font-family: monospace!important;
    background-color: #000000f0!important;
    color: #fff!important;
    text-shadow: 1px 1px 2px #00ff1a;
}

input,
.favoriteStickers,
textarea {
    border: solid 2px green!important;
}

.preview {
    font-size: 14px!important;
}

/*Liste des Sujets*/
.topic:nth-child(2n + 1) {
    background-color: #000f04!important;
}

.topic {
    background-color: #001704!important;
}


header {
    background-color: #000000f0!important;
}

/*Topics et messages*/
.bloc,
.message,
#menu {
    background-color: #001704fa!important;
    color: #00ff1a!important;
    outline: none;
    box-shadow: 0px 0px 8px #00ff1a!important;
    text-shadow: 0px 0px 10px #094b00;
    transition: background-color 1s, text-shadow 1s, box-shadow 1s;
}

.message:hover {
    background-color: #002802bd!important;
    text-shadow: 0 0 10px #00ff4d;
    box-shadow: 0 0 50px #00ff4d!important;
}

.topic-subject:hover,
.topic-left,
.right {
    background-color: #000;
    border-top: solid 0px!important;
}

.message-content {
    font-size: 18px!important;
    color: #73ff82!important;
}

.message.answer {
    background: #00ff1a;
}

._format._spoiler:not(.active) > div {
    background-color: #00ff1a!important;
    color: #00ff1a!important;
    text-shadow: none!important;
}

.see-more {
    background: linear-gradient(to bottom, rgba(213, 0, 192, 0) 0%, #00ff5540 65%)!important;
    color: #00ff1a!important;
}
}
