css:
@-moz-document domain("omegle.com")
{
.chatmsgwrapper, #othervideo, .sendbtn, .disconnectbtnwrapper, #intro
{
    backdrop-filter: blur(30px);
    background: transparent;
background-image: url(https://i.imgur.com/gGI11AA.png);
background-size: 187px !important;
}
* {
    color: white !important;
    border: none !important;
}
#selfvideo, #othervideo, .logwrapper, .videochat .controlwrapper, .videochat .disconnectbtn, .videochat .disconnectbtnwrapper, .chatmsgwrapper, .sendbtnwrapper, .disconnectbtnwrapper, #intro {
    background: #55555500;
    border-radius: 1em !important;
}
body {
background: url(https://i.imgur.com/WHE8ixZ.jpg);
    background-size: cover;
    background-attachment: fixed;
    background-position: top;
}
.chatmsg
{
background: #ffffff00;
margin: 5px !important;
}
.disconnectbtnwrapper.newbtn, .topictag, input[type="button"] {
background: linear-gradient(-45deg, #ff5b1f, #ff8e1f);
border-radius: 1em;
}
a[style*="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238); color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative;"], a[style*="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238); color: black; text-align: left; text-decoration: none; padding: 0.25em 0.25em 0.25em 2em; border-radius: 0.5em; position: relative;"]  {
    visibility: hidden;
    height: 0px;
}
img[src*="newchatbtn.png"] {
    visibility: hidden;
    width: 0px;
    margin-left: -22px;
}
.goog-te-gadget-simple, .topictageditor, #intro, #header {
    background: transparent;
    box-shadow: none;
}
#tagline img {
    filter: invert(1);
}
#logo {
    filter: invert(1) brightness(1.24) hue-rotate(180deg);
}
#header {
    background: black;
}
.topictageditor {
    background: #ffffff1c !important;
    border-radius: 1em;
}
.youmsg .msgsource {
    color: #2299ff !important;
}
.strangermsg .msgsource {
    color: #ff8e1f !important;
}
.disconnectbtn {
    background: transparent;
}
a#videobtnunmoderated {
    background: #00000059 !important;
}
div[style="position: relative; background-color: rgb(255, 255, 255); margin: 15% auto; padding: 1em; width: 50%; min-width: 300px; box-shadow: rgba(0, 0, 0, 0.333) 5px 5px 10px 5px; border-radius: 2em;"] {
    background: transparent !important;
    backdrop-filter: blur(30px);
background-image: url(https://i.imgur.com/gGI11AA.png);
}
#monitoringnotice h2 {
    background: #2299ff;
}
}
