css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
url-prefix("http://www.mediapart.fr/club/")
{
#content-top{display:none;}
}
@-moz-document
url-prefix("http://www.mediapart.fr/journal/international/"),
url-prefix("http://www.mediapart.fr/journal/france/"),
url-prefix("http://www.mediapart.fr/journal/economie/"),
url-prefix("http://www.mediapart.fr/journal/culture-idees/"),
url-prefix("http://www.mediapart.fr/club/")
 {
* {background-color: #ddd !important; color:#484848;}
#main{background:none !important;}
.bas, .barre_outils_bas_article, #bas_page, #footer, #navbar, #sidebar-left, #sidebar-right, #header, .jcarousel-control{display: none !important;}
.comment {background:none !important;}
#page, #page-inner, #main{width:100% !important;}
#main-inner{text-align:center !important; background:none !important;}
#content{width: 800px !important; float:none !important; display:inline-block; text-align:center;}
#mycarousel {width:800px !important; font-size:1.2em !important; line-height:1.3em;}
.jcarousel-list{width:100% !important;}
.jcarousel-clip{width:800px !important; height:100% !important;}
.jcarousel-item{width:800px !important;}
.boite_noire { width:800px !important;}
span.author_staff a { color:#090 !important;}
.date_article { color:black !important;}
div.article_voir_aussi, div.article_prolonger_fin {background:none !important;}
}

@-moz-document domain("www.mediapart.fr") {
*{font-family:sans-serif !important;line-height:1.3em;}
.page_papier_detail, .field-item, .content p{text-align:justify;}
}
