@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
#blueBar, .uiTextHighlight {background: rgba(50,50,50,.999) !important;}

.UIFullPage_Container, .home{ background: none #efefef fixed repeat center top!important; color:#555555 !important; }

#pageLogo a {background: url("http://cabaoo.com/bilder/hblogo.png") no-repeat center center, rgba(10,10,10,.999) !important; left: 0px !important; width: 107px !important;}

#pageLogo a:hover {background: url("http://cabaoo.com/bilder/hblogo.png") no-repeat center center, rgba(10,10,10,.999) !important; left: 0px !important; width: 107px !important;}

#jewelCase {left: 106px !important; background: rgba(10,10,10,.999) !important; width: 75px !important; height: 31px !important; top: 10px !important; }
#pageLogo {background: red !important;}
.jewel {height: 31px !important; margin: 0px !important; width: 23px !important; border: none !important; margin-right: 1px !important;}

#jewelAlert{background-position: -78px -137px !important;}
#jewelMail{background-position: -78px -73px !important;}
#jewelRequest{background-position: -78px -105px !important;}
.jewel a {background-color: transparent !important;}
.jewelToggler:hover {outline:none !important; background-color: #222 !important ;text-decoration:none !important;}
#jewelBoxMail li a:hover,
#jewelBoxMail li a:active,
#jewelBoxMail li a:focus {background: #627AAD !important;}

#jewelBoxMail {background: #FFF !important;}
.seeMore {border-top: solid 2px #E0E0E0 !important;}

}
