css:
@-moz-document url-prefix("https://www.google.com/"){
  /* Set the background color and font of the page */
  body {
    background-color: #C1C1C1;
    font-family: 'Arial', sans-serif;
  }

  /* Set the classic Google logo image and position */
  #logo {
    background-image: url(https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png) !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;
    height: 92px !important;
    width: 272px !important;
  }

  /* Hide the Google doodle */
  .hplogo {
    display: none !important;
  }

  /* Set the search box appearance */
  form[action="/search"] input[type="text"] {
    border: 1px solid #808080;
    border-radius: 2px;
    height: 21px;
    padding: 3px;
    width: 200px;
  }

  /* Set the search button appearance */
  form[action="/search"] input[type="submit"] {
    border: none;
    background-color: #C1C1C1;
    color: #000000;
    padding: 2px 10px;
    cursor: pointer;
  }

  /* Set the appearance of links */
  a.gb1, a.gb4 {
    color: #0000FF;
    text-decoration: underline;
    cursor: pointer;
  }

  /* Set the appearance of the footer */
  #fbar {
    background-color: #808080;
    color: #D3D3D3;
    font-size: 12px;
    padding: 10px;
    text-align: center;
    border-top: 1px solid #C1C1C1;
  }

  /* Set the text color to blue */
  body, input, button {
    color: #0000FF;
  }
}
