css:
html[dark], [dark] {
  --ytd-searchbox-legacy-border-color: #779749;
  --yt-spec-base-background: #0b0411;
  --yt-spec-raised-background: #09030e;
  --yt-spec-menu-background: #060209;
  --yt-spec-text-secondary: #c5b8d1;
  --yt-spec-text-disabled: #bcafc6;
  --yt-spec-themed-blue: #779749;
  --yt-spec-themed-green: #779749;
  --yt-spec-ad-indicator: #779749;
  --yt-spec-themed-overlay-background: #779749;
  --yt-spec-commerce-badge-background: #002d08;
  --yt-spec-static-brand-black: #0b0411;
  --yt-spec-static-grey: #0b0411;
  --yt-spec-static-overlay-background-solid: #0b0411;
  --yt-spec-static-overlay-background-medium: #0b0411;
  --yt-spec-touch-response-inverse: #0b0411;
  --yt-spec-icon-inactive: #0b0411;
  --yt-spec-icon-disabled: #0b0411;
}

html[dark] {
 --yt-live-chat-background-color: #0b0411;
  --yt-live-chat-action-panel-background-color: #0b0411;
  --yt-live-chat-action-panel-background-color-transparent: #0b0411;
  --yt-live-chat-secondary-background-color: #0b0411; 
  --yt-live-chat-toast-background-color: #0b0411;
  --yt-live-chat-mode-change-background-color: #0b0411;
  --yt-live-chat-text-input-field-inactive-underline-color: #0b0411;
  --yt-live-chat-text-input-field-placeholder-color: #0b0411;
  --yt-live-chat-disabled-button-background-color: #0b0411;
  --yt-live-chat-sub-panel-background-color: #0b0411;
}

html[darker-dark-theme], [darker-dark-theme] [light] {
    --yt-spec-text-primary: #decdec;
    --yt-spec-text-primary-inverse: #d8cbe4;
}

html[system-icons] {
    --yt-spec-icon-active-other: #0b0411;
    --yt-spec-icon-inactive: #0b0411;
    --yt-spec-icon-disabled: #0b0411;
    --yt-spec-brand-icon-active: #0b0411;
    --yt-spec-brand-icon-inactive: #0b0411;
    --yt-button-icon-button-text-color: var(--yt-spec-text-primary);
}

html:not(.style-scope) {
    --primary-text-color: var(--light-theme-text-color);
    --primary-background-color: var(--light-theme-background-color);
    --secondary-text-color: var(--light-theme-secondary-color);
    --disabled-text-color: var(--light-theme-disabled-color);
    --divider-color: var(--light-theme-divider-color);
    --error-color: #dd2c00;
    --primary-color: #3f51b5;
    --light-primary-color: #c5cae9;
    --dark-primary-color: #303f9f;
    --accent-color: #ff4081;
    --light-accent-color: #ff80ab;
    --dark-accent-color: #f50057;
    --light-theme-background-color: #fff;
    --light-theme-base-color: #000;
    --light-theme-text-color: #0b0411;
    --light-theme-secondary-color: #0b0411;
    --light-theme-disabled-color: #0b0411;
    --light-theme-divider-color: #dbdbdb;
    --dark-theme-background-color: #0b0411;
    --dark-theme-base-color: #fff;
    --dark-theme-text-color: #fff;
    --dark-theme-secondary-color: #bcbcbc;
    --dark-theme-disabled-color: #0b0411;
    --dark-theme-divider-color: #0b0411;
    --text-primary-color: var(--dark-theme-text-color);
    --default-primary-color: var(--primary-color);
}
