css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

.guided-help-box,
#yt-uix-clickcard-card1,
.yt-alert {
  display: none !important;
}



/*-----------------------
  GLOBAL
-------------------------*/
body {
  background: #0D0D0D !important;
  color: #eee !important;
}


#yt-masthead-container.yt-masthead-hh {
  background: #000 !important;
}



/*-----------------------
  BASE TEXT COLOR
-------------------------*/
.browse-container,
.comment-text {
  color: #eee !important;
}


/*-----------------------
  DARKER TEXT COLOR
-------------------------*/
.viewcount,
.view-count,
.feed-item-content .time-published {
  color: #aaa !important;
}

/* WHITE TEXT */
.yt-uix-button-default,
.epic-nav-item-heading {
  color: #fff !important;
}

.time-published,
.starting-box {
  color: #000 !important;
}



/*-----------------------
  LINKS
-------------------------*/
.guide-context-item .title,
.yt-tile-default.video-list-item a .title,
.video-list-item .title,
a:link {
  color: #CC3366 !important;
}
#guide-container a:hover .title,
.video-list-item a:hover .title,
a:visited,
a:hover {
  color: #FF99CC !important;
}

#guide-container a:hover .title {
  text-decoration: underline !important;
}




/*-----------------------
  CHANNEL NAVIGATION
-------------------------*/
.browse-videos .browse-content {
  border: 0 !important;
}
#yt-admin-sidebar a,
.navigation-menu .menu-item {
  border-top: 0 !important;
  border-bottom: 1px solid #444 !important;
}
.navigation-menu .menu-item a:hover {
  background: #222 !important;
}
.navigation-menu .menu-item .selected a,
.navigation-menu .menu-item a.selected {
  background: #222 !important;
}





/*-----------------------
  Logo
-------------------------*/
/*
#logo {
  width: 98px !important;  
  background: url(data:image/png;base64,) !important;
}
#logo:hover {
  background: url(data:image/png;base64,) top right !important;
}
*/




/*-----------------------
  Block Hover
-------------------------*/
.guide-context-item a:hover,
.guide-context-item.context-playing a,
.filter-content:hover,
.yt-lockup2:hover,
.branded-page-v2-body .feed-item-container:hover,
.lohp-shelf-cell-container:hover {
  background-color: #111 !important;
}





/*-----------------------
  White Text
-------------------------*/
#comments-view h4,
#comments-view h4 a,
#watch7-discussion,
#watch-description,
.watch-view-count,
.yt-uix-expander-head,
.filter-col-title,
.yt-lockup2 p,
.yt-lockup2-badges,
#guide-container .guide-item.guide-item-selected {
  color: #fff !important;
}




/*-----------------------
  Content Dark Background
-------------------------*/
#watch7-headline,
#watch7-notification-area,
#watch7-user-header,
#watch7-content,
#filter-dropdown,
#footer-container,
.primary-col,
.branded-page-v2-col-container,
.branded-page-v2-primary-col,
.branded-page-v2-secondary-column-hidden .branded-page-v2-col-container {
  background: #1a1a1a !important;
}

body #footer-hh-container {
  background-color: #000000 !important;
}





/*-----------------------
  Borders
-------------------------*/
#yt-masthead-container.yt-masthead-hh,
#watch7-headline,
#watch7-notification-area,
#watch7-user-header,
#comments-view hr,
#watch7-discussion,
#watch7-action-buttons,
#watch7-action-panels,
#filter-dropdown,
.search-header,
.primary-col,
.feed-header,
.feed-item-container .feed-item-main,
#channel-subheader,
.c4-box,
#footer-hh-main,
body #footer-hh-container,
.branded-page-v2-col-container-bottom-border,
.lohp-pyv-shelf-container,
.guide-section-separator,
.lohp-large-shelf-container .lohp-blog-headline,
.branded-page-v2-col-container,
.lohp-newspaper-shelf,
.lohp-large-shelf-container,
.lohp-shelf-cell-container,
.branded-page-v2-primary-col,
.branded-page-v2-subnav-container,
#c4-header-bg-container,
#footer-container,
#footer-main,
.branded-page-v2-secondary-column-hidden .branded-page-v2-primary-col {
  border-color: #2C2C2C !important;
}




/*-----------------------
  Sexy Buttons
-------------------------*/
#footer-main .yt-uix-button,
#video-page-subnav .yt-uix-button,
.yt-uix-load-more,
.yt-uix-pager .yt-uix-button {
  color: #fff !important;
  font-weight: bold !important;
  text-shadow: 1px 1px 2px #000;
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.3) !important;
  -moz-box-shadow: inset 0 1px 1px rgba(0,0,0,.3) !important;
  -ms-box-shadow: inset 0 1px 1px rgba(0,0,0,.3) !important;
  -o-box-shadow: inset 0 1px 1px rgba(0,0,0,.3) !important;
  box-shadow: inset 0 1px 1px rgba(0,0,0,.3) !important;
  background: -moz-linear-gradient(top, #AF2052, #6F072B) !important;
  background: -webkit-gradient(linear, left top, left bottom, to(#6F072B), from(#AF2052)) !important;
  border-color: #9F1D4A !important;
}
#footer-main .yt-uix-button span,
#video-page-subnav .yt-uix-button span,
.yt-uix-load-more span,
.yt-uix-pager .yt-uix-button span {
  color: #fff !important;
  text-shadow: 1px 1px 0  #000;
}
#footer-main .yt-uix-button:hover,
#video-page-subnav .yt-uix-button:hover,
.yt-uix-load-more:hover,
.yt-uix-pager .yt-uix-button:hover {
  color: #fff !important;
  font-weight: bold !important;
  text-shadow: 1px 1px 2px #000;
  background: -moz-linear-gradient(top, #333, #111) !important;
  background: -webkit-gradient(linear, left top, left bottom, to(#111), from(#333)) !important;
  border-color: #333 !important;
  -moz-box-shadow: 0 0 0 #aaa !important;
}

.yt-uix-pager .yt-uix-button-toggled {
  border-color: #333 !important;
  background: -moz-linear-gradient(top, #111, #333) !important;
  background: -webkit-gradient(linear, left top, left bottom, to(#333), from(#111)) !important;
}
.yt-uix-slider.browse-collection .yt-uix-pager {
  border-color: #9F1D4A !important;
}

