css:
@-moz-document url(https://storium.com) {
  body {
    background-color: #333 !important;
    color: #CCC !important;
  }

  .header-stack {
    background-color: #333 !important;
  }

  .header-stack>.content>h1>a>img {
    filter:
      brightness(100%) grayscale(100%)
      opacity(0.1)
      drop-shadow(0 0 0 #CCC)
      drop-shadow(0 0 0 #CCC)
      drop-shadow(0 0 0 #CCC)
      drop-shadow(0 0 0 #CCC);
  }

  #page {
    background-color: #333 !important;
  }

  .details-content {
    background-color: #333 !important;
  }

  .tab {
    background-color: #333 !important;
    color: #CCC !important;
  }

  .smallcap {
    color: #CCC !important;
  }

  .general-avatar-bubble {
    background-color: #666 !important;
    color: #CCC !important;
  }

  .bubble {
    background-color: #666 !important;
    color: #CCC !important;
  }

  .attributes-item {
    background-color: #666 !important;
    color: #CCC !important;
  }

  .table-striped>tbody>tr:nth-child(odd)>td, tr.found {
      background-color: transparent !important;
  }

  .l-title {
     color: #CCC !important;
  }

  .l-tab {
    background-color: #333 !important;
    border-color: #666 !important;
  }

  .l-content {
    background-color: #333 !important;
    border-color: #666 !important;
  }

  a {
    color: #0f8cb1;
  }

  a:hover {
    color: #22b5e1
  }

  .content>div>div>p {
    color: #CCC !important;
  }

  .content>p {
    color: #CCC !important;
  }
}
