css:
@-moz-document domain(pokernow.club) {
.table-player-7, 
.table-player-8,
.table-player-9,
.table-player-10,
.table-player-11,
.landscape .version-link-in-game, 
.landscape .conf-controls,
.landscape .help-link-game {
  display: none;
}

.landscape .table-player-1 {
  top: 80%;
  left: 10%;
}

.landscape .table-player-2 {
  top: 41%;
  left: 2%;
}

.landscape .table-player-3 {
  top: 2%;
  left: 10%;
}

.landscape .table-player-4 {
  top: 2%;
  left: 70%;
}

.landscape .table-player-5 {
  top: 41%;
  left: 80%;
}

.landscape  .table-player-6 {
  top: 80%;
  left: 70%;
}

.landscape .player-with-video .video-container {
  height: 5.3rem;
}

.landscape .table-player-1  .table-player-bet-value,
.landscape .table-player-2 .table-player-bet-value,
.landscape .table-player-3 .table-player-bet-value {
  top: 14%;
  left: 102%;
}

.landscape .table-player-4 .table-player-bet-value,
.landscape .table-player-5 .table-player-bet-value,
.landscape .table-player-6 .table-player-bet-value {
  top: 14%;
  left: -32%;
}

.landscape .table-player-1.player-with-video .table-player-bet-value,
.landscape .table-player-2.player-with-video .table-player-bet-value,
.landscape .table-player-3.player-with-video .table-player-bet-value,
.landscape .table-player-4.player-with-video .table-player-bet-value,
.landscape .table-player-5.player-with-video .table-player-bet-value,
.landscape .table-player-6.player-with-video .table-player-bet-value {
  top: 14%;
  left: 0px;
}


.landscape .chat { 
  left: 50%;
  transform: translateX(-50%);
}

.landscape  .aux-chat-bottom-buttons-ctn {
  top: 92%;
  right: 64%;
}

.landscape .player-with-video .video-container{
  position: absolute;
  top: 127%;
  left: 0%;
  width: 100%;
}

.landscape.player-with-video .table-player-status-icon {
  color: #989898;
  text-transform: uppercase;
  font-size: .4rem;
  position: absolute;
  bottom: 0.2rem;
  left: 1rem;
  z-index: 3;
  text-align: center;
  line-height: 1.75;

  /* Overrides */
  border-radius: 0;
  padding-left: initial;
  padding-top: initial;
  padding-right: initial;
  right: initial;
  height: initial;
  top: initial;
  box-shadow: initial;
  background: initial;
  width: initial;
}

.landscape .player-table-signals-container.top {
  right: -0.3rem;
}

.landscape .player-with-video .table-player-cards { 
	left: 3.2rem;
}

.landscape .player-with-video .player-table-signals-container.top {
  right: -0.4rem;
}

.landscape .player-with-video .table-player-name-container,
.landscape .player-with-video .table-player-stack {
  left: 0;
  width: 100%;
  text-align: center;
}

.landscape .player-with-video .table-player-name-container p {
  text-align: center;
}

.landscape .player-table-signals-container .signal {
	margin-left: 0.6rem;
	margin-bottom: 0.4rem;
}

.landscape .dealer-button-ctn.dealer-position-1 {
    top: 19rem;
    left: 16rem;
}

.landscape .dealer-button-ctn.dealer-position-2 {
    top: 15rem;
    left: 12rem;
}

.landscape .dealer-button-ctn.dealer-position-3 {
     top: 5rem;
    left: 16rem;
}

.landscape .dealer-button-ctn.dealer-position-4 {
  	top: 5rem;
    left: 35rem;
}

.landscape .dealer-button-ctn.dealer-position-5 {
    top: 15rem;
    left: 41rem;
}

.landscape .dealer-button-ctn.dealer-position-6 {
    top: 19rem;
    left: 35rem;
}


}
