css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {


.right_column_container{width:99%!important;}

.right_column{margin-left:220px!important; margin-right:20px!important;float:none!important;width:auto!important;}

.left_column_container, #leftCol{left:10px!important;position:absolute;}

.like_box, .ufi_section{width:100%!important;}

.DOMControl_autogrow, .add_comment_text  {float:none!important;width:90%!important;left:100px!important;}
.UIFormButton{float:left!important;}

#pagelet_intentional_stream{padding:10px 20px!important;}

.UIStory{padding:15px 0!important;}

.GenericStory_Message{color:#101010!important;font-family:verdana, "lucida grande", tahoma, san-serif!important;font-size:13px!important;}

a{color:#627AAD!important;}
a:hover{color:#2A4280!important; text-decoration:none!important;}

#navSearch{width:400px!important;}

.friend:hover{color:#f0f0f0!important;}

.UIIntentionalStory_Message, .commentable_item, .UIStoryAttachment{padding-left:60px!important;}

.profile_top_wash, .profile_color_bar, #content, #globalContainer{width:100%!important;}

#contentCol{margin-left:192px!important;}
#headNavOut, #pageFooter{margin-left:191px!important;}

#pageLogo a, #jewelCase{margin-left:10px!important;}

#pageLogo a{background-position:-21px -0px!important;background-color:transparent!important;}
#pageLogo a:hover{background-color:#4B67A1!important;background-position:-21px -31px!important;}

#gb_content_and_toolbar{width:100%!important;}
.GBThreadMessageRow_Body{width:100%!important;}

#footerRight{right:20px!important;}

.comments_add_box .DOMControl_placeholder{width:90%!important;float:left!important;}

.pop_container_advanced{top:0!important;left:0!important;width:100%!important;height:100%!important;position:fixed!important;}

.pop_content{top:20%!important;width:700px!important;left:0!important;right:0!important;margin-left:auto!important;margin-right:auto!important;position:fixed!important;}

#q{width:95%!important;}

#pageNav a{color:#fff!important;}
#pageNav ul li a{color:#627AAD!important;}
#pageNav ul li a:hover, .openToggler #navAccountLink:focus, .openToggler #navAccountLink:active {color:#2A4280!important; text-decoration:none!important;}

.Mentions_Input {max-height:200px!important;overflow:auto!important;min-height:36px!important;}


#home_stream{margin:0!important;width:100%!important;}
}
