@-moz-document domain("calculator.net") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#topmenu ul #menuon a,
#topmenu ul li a, #topmenu ul li a:visited,

.cbcontainer .cbmark:after,
.cbcontainer input:checked ~ .cbmark, .cbcontainer input:checked ~ .rbmark,
.cbmark,
.h2result,
.rbmark,

[style="background:#eee;border:1px solid #ccc;padding: 3px;margin:3px;"],
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.cinfoT td:nth-child(2),
.cinfoT td:nth-child(3)
{
	text-align: center;
}

/* Fonts 
_____________________________________________________________________________________________________*/

body, p, td, div, span, th, li, textarea
{
	font-size: 1.52rem!important;
}

input
{
	font-size: .99rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

[id*='div-gpt-ad-']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.inuipoundspan {
    margin-left: 36px;
}

.inuiinchspan {
    margin-left: 18px;
}

.inuickspan,
.inuifootspan {
    margin-left: 13px;
}

/* Margins 
_____________________________________________________________________________________________________*/

table#calinputtable,
table#standardheightweight
{
	margin: 1.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.cinfoHd,
.cinfoHdL,
.scifunc,
.scinm,
.sciop,

[align="center"]
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

table#sciout
{
	height: 70%!important;
	width: 70%!important;
}

#contentout
{
	width: 98%!important;
}

#content
{
	width: 85%!important;
}

.scird {
    width: 13rem!important;
}

.panel2 td[width="120"] {
    width: 200px!important;
}

}

@-moz-document domain("calculator.net") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
#homelistwrap {
    grid-template-columns: 350px 350px 350px 350px;
}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
