@-moz-document domain("calculator.net") {
/* Font Settings */

#breadcrumbs a, #breadcrumbs a span,
a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#breadcrumbs a:hover,
#breadcrumbs a span:hover,

.scifunc:hover,
.scinm:hover,
.sciop:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
div,
input,
li,
p,
span,
table.cinfoT td,
td,
textarea,
th
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

[align="right"][bgcolor="#E0F0FE"] td b,
table.cinfoT td.cinfoHdL,
table.cinfoT td.cinfoHd,
legend,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

b,
h3,
h4,
table.cinfoT tr:nth-child(even) td:first-child,
table.cinfoT tr:nth-child(odd) td:first-child
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

[colspan="2"],
h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

[align="right"] td:nth-child(2),
[style*="font-size:11px;color:#666;cursor:default;line-height:14px;fill:#666;"] tspan,
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

