@-moz-document domain("calculator.net") {
/* Image Settings */

.cbcontainer,
.hl,

[align="right"],
[bgcolor="#dddddd"],
li,
p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.cbcontainer span input:checked+label, 
.cbcontainer:hover,
.hl:hover,

[align="right"]:hover,
[bgcolor="#dddddd"]:hover,
li:hover,
p:hover
 {
	filter: brightness(100%)!important;
}


/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

selector:hover
 {
	opacity: 1!important;
}

}

@-moz-document domain("calculator.net") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Dice ___________________________________________________________________________*/

[id*='rdice']
 {
    width: 125px!important;
}

/* Die 1 */

