css:
@-moz-document url(https://musescore.org) {
  body {
  background-color: #1a1f30;
}

.page-front #row2 .button-wrapper .button {
  background-color: #000;
  border-radius: 5px;
}

.content-header {
  background-color: #000;
}

a {
  color: #fff;
}

.content-header {
  border-color: #1a1f30;
}

.page-front #row3 {
  background-color: #0F131F;
}

.page-front #row3 .instrument {
  background-color: #000;
  border-radius: 5px;
}

.page-front #row5 {
  background-color: #0F131F;
}

.page-front #row5 .block {
  background-color: #000;
}

.download-button {
  background: #000;
}

#hero-section {
  background: linear-gradient(90deg,rgba(0,0,0,0.3) 0%,transparent 67.84%),linear-gradient(270.01deg,#552ef4 0.01%,#c05aff 33.34%,#552ef4 68.82%,#220961 100%) repeat-x;
}

.page>header {
  visibility: hidden;
}

header {
  width: 0px;
  height: 0px;
}

.content-header {
  margin-top: -3.5%;
}
}
