@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
  /* Set the background image */
  body {
    background-image: url("https://drive.google.com/uc?export=view&id=1HH3vof1eOBgt62RxjUraJeR5xYtiW33W");
    background-size: 100% 100%;
    background-position: center center;
    background-repeat: no-repeat;
  }

  /* Change the font color to white */
  body, .linkWrap.noCount, .uiHeaderTitle {
    color: white !important;
  }

  /* Change the button color to black and pink */
  ._42ft, ._42fr {
    background-color: #000000 !important;
    color: #f5a5c9 !important;
  }

  /* Change the active button color to pink */
  ._42ft:hover, ._42fr:hover {
    background-color: #f5a5c9 !important;
    color: #000000 !important;
  }

  /* Change the Facebook logo to a Blackpink logo */
  #u_0_h {
    background-image: url("https://drive.google.com/uc?export=view&id=1hb28wlqdKsdO6B_5JfAmq0Ile-K9Z8hb");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center center;
  }
}
