/* i really want this to be global */

@-moz-document domain(ku-portal.kyushu-u.ac.jp) {

    /* your code goes here */

    body {
        background: none;
    }

    #container,
    #content,
    #header,
    #footer {
        max-width: 1150px;
        width: 100%;
        min-width: 950px;
    }
    
    #container{
        padding-top: 16px;
    }
    
    #footer {
        background: none
    }

    #top_menu {
        background: none;
    }

    #head_base {
        background-image: none;
    }

    #main,
    #sub1 {
        font-family: "游ゴシック Medium", "Yu Gothic Medium", "游ゴシック体", "YuGothic", "ヒラギノ角ゴ ProN W3", "Hiragino Kaku Gothic ProN", "メイリオ", "Meiryo", "verdana", sans-serif;
        font-size: 1.1em;
    }

    .portletArea {
        margin: 0 16px 32px 0;
    }

    .portletArea .details {
        height: 24px;
        padding: 4px 0;
    }
}
