css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("salesforce.com") {

/*changes table header to white*/
body .pbBody table.list tr.headerRow td, body .pbBody table.list tr.headerRow th{
    background: none !important;
    border-width:0 0 3px 0 !important;
    font-size: 1em !important;
}

/*Makes grey of pageblocks a little darker*/
.secondaryPalette.bPageBlock  {
    background-color:#f2f2f2!important;
}

/*Makes empty page block section darker */
body .bRelatedList table.list tr.headerRow th.noRowsHeader {
     background-color:#f2f2f2!important;
}

/*Makes fields closer togeher in detail section*/
body .bPageBlock .pbBody .labelCol, body .bPageBlock .pbBody .dataCol, body .bPageBlock .pbBody .data2Col {
    padding-bottom:2px !important;
    padding-top:2px !important;
}

/*Makes page block seperates smaller but line bigger*/
body .bPageBlock .pbBody .pbSubheader {
    border-width:4px 0 0 !important;
    padding:2px 2px 2px 5px !important;
    -moz-border-radius: 3px;
}

/*Makes seperation between page blocks smaller*/
.pbSubheader {
    margin-top:5px !important;
}

/* Makes pageblock section label smaller */
body .bPageBlock .pbBody .pbSubheader h3, body .bWizardBlock .bPageBlock .pbBody .pbSubheader label {
    font-size:1.1em !important;
}

/*Makes label name darker in detail section */
body .bPageBlock .pbBody .labelCol {
    color:black !important;
} 

/*Gives pageblocks more seperation*/
body .bPageBlock {
    margin-bottom:13px !important;
}

/*Makes field seperator in detail section darker */
body .bPageBlock .detailList tr td, body .bPageBlock .detailList tr th, body table.list tr td, body table.list tr th, body .hoverDetail .bPageBlock .detailList tr td, body .hoverDetail .bPageBlock .detailList tr th {
    border-color:#D4DADC !important ;
}

/* Gives page block sections a thicker border */
body .bPageBlock, body #bodyCell .bResource .secondaryPalette, body .secondaryPalette.bPageBlock, body .overlayDialog .secondaryPalette.bPageBlock, body .individualPalette .secondaryPalette.bPageBlock, body .bodyDiv .genericTable, body .genericPageBlockTable, body .bodyDiv .bSubBlock, body .bComponentBlock .bPageBlock, body .bMyDashboard .bPageBlock, body.rlHoverFrame .bPageBlock, body.subjectSelectionPopup div.choicesBox, body.lookupTab .secondaryPalette.bPageBlock, body.popupTab .secondaryPalette.bPageBlock, body.UserTagStatsPage .secondaryPalette.bPageBlock {
    border-bottom:3px solid #EAEAEA !important;
    border-left:3px solid #EAEAEA !important;
    border-right:3px solid #EAEAEA !important;
}
   
/* Uncomment to make page block seperators stick out more 
body.opportunityTab .tertiaryPalette, .individualPalette .opportunityBlock .tertiaryPalette  {
    background-color:#f0db88!important;
    border-color:#f0db88!important;
}
*/

/*Makes title of related list slightly larger */
body .bRelatedList .pbTitle h3, body .bPageBlock .pbTitle h2, body .bPageBlock .pbTitle h3, body .bSubBlock h3.lbHeader {
    font-size:1.4em !important;
}

/*Additonal styling for detail section*/
#ep{
    background-color: #f5f5f5 !important;
    border-top:3px solid #EAEAEA !important;

}

.bSubBlock.secondaryPalette{
    background-color: #f2f2f2 !important;
}

}
