css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.pcmasters.de/forum/") {
    div.content_complete_out {
        width:auto !important;
    }
    div.content_complete_out>table {
        margin-top:0px !important;
    }
    div.content_complete_out>br {
        height:1px !important;
        line-height: 1px !important;
    }
    div.page>div>br {
        display:none;
    }
    div.page>div>table[cellpadding="7"] td{
        padding-top:1px !important;
        padding-bottom:1px !important;
    }
    span.navbar{
        padding-top:0px !important;
    }
    div.pagenav>table{
        margin-bottom:1px !important;
    }
    table#threadslist{
        border-collapse:collapse;
        border-spacing:0px !important;
    }
    table#threadslist tr td {
        padding:2px !important;
        border:1px solid #777;
    }
    img[src="imagesv2/buttons/firstnew.gif"]{
        width:16px;
        height:16px;
    }
    table#threadslist tr td div.smallfont {
        text-align:right;
    }
    table#threadslist tr.forum_home_hover>td:first-child+td>img{
        display:none;
    }
    table#threadslist tr.forum_home_hover>td:first-child{
        border-right:0px none;
    }
    table#threadslist tr.forum_home_hover>td:first-child+td{
        width:0px !important;
        padding:0px !important;
        border:0px none !important;
    }
}
