css:
@-moz-document domain("e-usr.ro") {  
:root {
    --blue: #00a6ff;
    --indigo: #6610f2;
    --purple: #6f42c1;
    --pink: #e83e8c;
    --red: #dc3545;
    --orange: #fd7e14;
    --yellow: #ffc107;
    --green: #28a745;
    --teal: #20c997;
    --cyan: #17a2b8;
    --white: #fff;
    --gray: #6c757d;
    --gray-dark: #343a40;
    --primary: #00a6ff;
    --secondary: #6c757d;
    --success: #28a745;
    --info: #17a2b8;
    --warning: #ffc107;
    --danger: #dc3545;
    --light: #f8f9fa;
    --dark: #343a40;
    --breakpoint-xs: 0;
    --breakpoint-sm: 576px;
    --breakpoint-md: 768px;
    --breakpoint-lg: 992px;
    --breakpoint-xl: 1200px;
    --font-family-sans-serif: "Fira Sans",sans-serif;
    --font-family-monospace: SFMono-Regular,Menlo,Monaco,Consolas,"Liberation Mono","Courier New",monospace;
}

a {
    background-color: transparent;
    color: #6610f2;
    text-decoration: none;
}

.navbar .navbar-nav .nav-link, .navbar-light .navbar-nav .nav-link {
    color: rgba(0,0,0,.5);
}

.navbar-expand-md .navbar-collapse {
    display: flex!important;
    flex-basis: auto;
    border: 0px solid #28a745;
}

body {
    background-color: #343a40;
    color: #212529;
    font-family: Fira Sans,sans-serif;
    font-size: .85rem;
    font-weight: 400;
    line-height: 1.428571429;
    margin: 0;
    text-align: left;
}

.navbar {
    background-color: #00a6ff;
    box-shadow: 0 2px 4px rgba(0,0,0,.04);
}

.card {
    word-wrap: break-word;
    background-clip: border-box;
    background-color: #00a6ff;
    border: 4px solid #6f42c1;
    border-radius: 0.25rem;
    display: flex;
    flex-direction: column;
    min-width: 0;
    position: relative;
}

.card-avizier-filiala-locala img {
    border-bottom: 1px solid #002a58!important;
}

.card-body {
    flex: 1 1 auto;
    min-height: 1px;
    padding: 1.25rem;
}

.btn-link {
    color: #6610f2;
    font-weight: 400;
    text-decoration: none;
}
}
