@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

/** Drop Shadow for Avatar **/
img.kh {
  border: 1px solid black !important;
  margin: 2px 5px 5px 0 !important;
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.8) !important;
  -webkit-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.8) !important;
  box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.8) !important;
}

/** Menu **/
.NE .Ts { display: none }
.NE {
  background:url(data:image/png;base64,) no-repeat center right !important;
  padding: 4px 16px 4px 4px !important;
  margin-bottom: -3px !important;
}
.NE:hover, .Tt .NE {
  background:url(data:image/png;base64,) no-repeat center right !important;
  padding: 4px 16px 4px 4px !important;
  margin-bottom: -3px !important;
}

/** SMILEYS **/
/* Univeral values for all smileys */
img[iconset] { vertical-align: middle; }

img[alt=":)"], img[alt=":-)"], img[alt="=)"], .jn { /* smile & smiley menu icon */
  background: url(data:image/png;base64,) !important;
  vertical-align: middle;
  width: 16px !important;
  height: 16px !important;
}
img[alt=":P"], img[alt=":-P"], img[alt="=p"] { /* tongue */
  background: url(data:image/png;base64,) !important;
  width: 16px !important;
  height: 16px !important;
}
img[alt=":("], img[alt=":-("], img[alt="=("] { /* sad */
