css:
@-moz-document domain(bard.google.com) {
:root{
  --v-ani-mode: rbw2;
  --v-ani-time: 3.5s;
  --ani-rbw2-col: #ff3300;
}

body, .mat-drawer-inner-container.ng-tns-c1668312745-0, .mat-mdc-nav-list.mat-mdc-list-base.mdc-list.gmat-body-2.nav-items.ng-tns-c1668312745-0.gmat-mdc-list.ng-star-inserted > button, .factuality-container,.mat-mdc-menu-content.mdc-list{
  background-color: black !important;
  color: white !important;
}

.gb_Bd.gb_Rd.gb_Id.gb_Hd, mat-sidenav, .mat-mdc-list-item.mdc-list-item.mat-mdc-list-item-interactive.mdc-list-item--with-leading-icon.mat-mdc-list-item-single-line.mdc-list-item--with-one-line, div.card,.mat-mdc-text-field-wrapper.mdc-text-field.ng-tns-c491895417-1.mdc-text-field--outlined.mdc-text-field--no-label,mat-form-field, .mat-expansion-panel.model-response-previews.ng-star-inserted, .multi-response-container, .response-container.ng-star-inserted, .mat-expansion-panel.model-response-previews.ng-tns-c3728856580-9.ng-star-inserted, .mat-expansion-panel.ng-star-inserted.mat-expanded.mat-expansion-panel-spacing, mat-expansion-panel-header, .mat-expansion-panel-body,.waitlist-text.top-text,.waitlist-text.bottom-text,chat-window.ng-star-inserted,.main,flex-window-container.ng-star-inserted,faq-window.ng-star-inserted>flex-window-container,.footer.ng-star-inserted,.mat-drawer-content.mat-sidenav-content{
  background-color: black !important;
}

.chat-history.ng-star-inserted>div>user-query>div>div>h2,div.markdown>p, .mat-mdc-list-item.mdc-list-item.mat-mdc-list-item-interactive.mdc-list-item--with-leading-icon.mat-mdc-list-item-single-line.mdc-list-item--with-one-line>span>span, .mat-icon.notranslate.mat-mdc-list-item-icon.google-material-icons.mat-icon-no-color.mdc-list-item__start,.mat-drawer-inner-container.ng-tns-c1668312745-0>mat-nav-list>button>span>span, .mat-drawer-inner-container.ng-tns-c1668312745-0>mat-nav-list>button>mat-icon, .response-container.ng-star-inserted>div>div>zero-state>div,.sentence-prefix, textarea,.mat-panel-title-wrapper,.mat-mdc-menu-content.mdc-list>div>button>mat-icon.mat-icon.notranslate.google-material-icons.mat-icon-no-color,.mat-mdc-menu-item.mat-mdc-focus-indicator.mdc-list-item.ng-star-inserted>mat-icon,.mat-mdc-tooltip-trigger.copy-button.mdc-icon-button.mat-mdc-icon-button.gmat-mdc-button-with-prefix.mat-unthemed.mat-mdc-button-base.gmat-mdc-button,.mat-icon.notranslate.mat-mdc-list-item-icon.google-symbols.mat-icon-no-color.mdc-list-item__start{
  color: white !important;
}

.mat-divider.mat-divider-horizontal{
  background-color: rgba(0,0,0,0.75) !important;
}

ul, .gmat-overline, li:before, .gmat-caption{
  color: darkgray !important;
}

div.chat-history.ng-star-inserted::after{
  background-color: rgba(0,0,0,0) !important;
}

.welcome-container.ng-star-inserted, .faq-container, chat-window.ng-star-inserted>div.chat-container.desktop.ng-star-inserted,.window-container.desktop,.mdc-snackbar__surface,.waitlist-content,code{
  background-color: hsl(0,0%,11%) !important;
}

.footer.ng-star-inserted>a>img{
  background-color: white !important;
}

code,.faq-button-ogb{
  color: gray !important;
}

div.welcome-body>p.ng-star-inserted>span.ng-star-inserted,div.welcome-body>p.ng-star-inserted>strong,h1.headline,h2.card-title,div.card>p, .mat-expansion-toggle-indicator-after.ng-star-inserted>span>mat-panel-title, h1.gmat-headline-1, .mat-expansion-panel-body,button.mat-mdc-tooltip-trigger.mdc-button.mdc-button--outlined.mat-mdc-outlined-button.gmat-mdc-button-with-prefix.mat-unthemed.mat-mdc-button-base.gmat-mdc-button.ng-star-inserted>span.mdc-button__label,.waitlist-text.top-text,.waitlist-text.bottom-text,.waitlist-content>h1,li,.mdc-button.mdc-button--unelevated.mat-mdc-unelevated-button.mat-primary.mat-mdc-button-base.gmat-mdc-button>.mdc-button__label{
  color: graytext !important;
}

div.chat-history.ng-star-inserted::before{
  border: none !important;
}

@keyframes rbw1{
  0% {color: #00ff00}
  9.09% {color: #0099ff}
  18.18% {color: #001aff}
  27.27% {color: #a200ff}
  36.36% {color: #ff0055}
  45.45% {color: #ff0000}
  54.54% {color: #ffff00}
	63.63% {color: #ff0000}
  72.72% {color: #ff0055}
  81.81% {color: #a200ff}
  90.90% {color: #001aff}
	100% {color: #0099ff}
}

@keyframes rbw2{
  0%{color: var(--ani-rbw2-col);}
  0%{
    filter: hue-rotate(0deg);
  }
  100%{
    filter: hue-rotate(360deg);
  }
  100%{color: var(--ani-rbw2-col);}
}

.gb_Ad.gb_Ld.gb_Hd.gb_De.gb_te.gb_Ae:before{
  content: "Bard Dark Theme V0.7" !important;
  animation: var(--v-ani-mode) var(--v-ani-time) infinite linear !important;
  margin-right: 10px !important;
  font-weight: bold !important;
}

.mat-mdc-list-item.mdc-list-item.mat-mdc-list-item-interactive.mdc-list-item--with-leading-icon.mat-mdc-list-item-single-line.mdc-list-item--with-one-line:hover,.gb_Ic:hover,.gb_e:hover{
  background-color: hsl(0,0%,10%) !important;
}

.faq-button-ogb:hover{
  background-color: hsl(0,0%,10%) !important;
  border-radius: 5px !important;
}

.code-block-wrapper.header.gmat-subhead-2{
  background-color: rgb(50,50,50) !important;
  color: white !important;
}

.code-block-wrapper.footer.gmat-overline{
  background-color: rgb(50,50,50) !important;
}

.footer.ng-star-inserted{
  margin: 0;
}

.main>.footer.ng-star-inserted>.google-logo>img{
  background-color: rgb(50,50,50) !important;
}

#gbwa>.gb_e:hover,.gb_e.gb_7a.gb_7f.gb_v:hover{
  border-radius: 25px;
}
}
