/* ==UserStyle==
@name           Spotify Nord
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Spotify Theme that follows Nord guidelines
@author         RyZn1
@license        GNU GPLv3
==/UserStyle== */

@-moz-document domain("spotify.com") {
    :root {
        --nordBG: rgba(59, 66, 82, 100%);
        --nordBG2: rgba(46, 52, 64, 100%);
        --nordGreen: rgba(163, 190, 140, 100%);
        --nordWhite: rgba(236, 239, 244, 100%);
        --nordRed: rgba(191, 97, 106, 100%);
        --nordBlue: rgba(129, 161, 193, 100%);
    }

    /*Top Bar*/
    .Root__main-view, .HsbczDqu9qjcYr7EIdHR {
        background-color: var(--nordBG)!important;
        background-image: none;
    }
    
    /*Top Bar Scrolled*/
    .T1xI1RTSFU7Wu94UuvE6 {
        background-color: var(--nordBG)!important;
    }
    
    .EvIR4O7jOSbNmxtMdIQ0 {
        display: none;
    }
    
    /*Play Bar*/
    .GD2gbRtcs5dOjMGAM_Y4 {
        background-color: var(--nordBG2)!important;
    }
    
    /*Side Menu*/
    .tUwyjggD2n5KvEtP5z1B, .Y8edH1Yjo4xrW_58czQj {
        background-color: var(--nordBG2);
        background-image: none;
    }
    
    /*Top background of playlist*/
    .CoLO4pdSl8LGWyVZA00t, .gHImFiUWOg93pvTefeAD, .contentSpacing, .koyeY6AgGRPmyPITi7yO {
        background-color: var(--nordBG)!important;
        background-image: none!important;
    }
    
    /*Song Panels*/
    .LunqxlFIupJw_Dkx6mNx, .LunqxlFIupJw_Dkx6mNx:hover {
        background-color: var(--nordBG);
    }
    
    /*Play Buttons*/
    .ButtonInner-sc-14ud5tc-0 {
        background-color: var(--nordGreen)!important;
    }
    
    /*Shuffle Button*/
    .OF_3F0SQCsBtL1jSTlTA {
        color: var(--nordRed)!important;
    }
    
    /*Scrub Bar Fill*/
    .epWhU7hHGktzlO_dop6z {
        background-color: var(--nordBlue)!important;
    }
    
    /*Scrub Bar Background*/
    .NoOAOv6U6vtqj_ybS1Cd {
        background-color: var(--nordBG2);
    }
}
