css:
@-moz-document url-prefix("https://chat.openai.com/chat") {
  /* Hide Chat Sidebar */
  #chat-sidebar {
    display: none !important;
  }

  /* Toggle Chat Button */
  #toggle-chat-button {
    position: fixed;
    bottom: 20px;
    right: 20px;
    z-index: 9999;
    padding: 10px 15px;
    font-size: 16px;
    background-color: #337ab7;
    color: #fff;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }

  #toggle-chat-button:hover {
    background-color: #23527c;
  }

  #toggle-chat-button:focus {
    outline: none;
  }

  #toggle-chat-button.visible {
    display: block;
  }

  #toggle-chat-button.hidden {
    display: none;
  }
}
