@-moz-document domain("chat.openai.com") {

/* Set black background */
body {
  background-color: #000;
}

/* Set rainbow gradient button that glows for chat buttons */
.chat-window-tab {
  background-image: linear-gradient(to bottom, #ff0000, #ff7f00, #ffff00, #00ff00, #0000ff, #4b0082, #8b00ff);
  background-size: 200% 200%;
  animation: rainbow-gradient-glow 5s ease infinite;
  color: #fff;
}

/* Set rainbow gradient that glows for chat bar */
.chat-bar {
  background-image: linear-gradient(to right, #ff0000, #ff7f00, #ffff00, #00ff00, #0000ff, #4b0082, #8b00ff);
  background-size: 200% 200%;
  animation: rainbow-gradient-glow 5s ease infinite;
}

/* Animation for rainbow gradient glow */
@keyframes rainbow-gradient-glow {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

}
