css:
.board {
  background-color: #010312;
  border: 3px solid #6699cc;
  box-shadow: 0 0 10px #6699cc;
}

.square-light {
  background-color: #333333;
}

.square-dark {
  background-color: #0a0e2b;
}

.piece {
  position: relative;
  margin: auto;
  font-size: 3em;
  font-weight: bold;
  color: #6699cc;
  text-shadow: 0px 0px 3px #fff;
}

.piece:before {
  content: attr(data-piece);
}

.piece-king:before {
  content: "\2654";
}

.piece-queen:before {
  content: "\2655";
}

.piece-rook:before {
  content: "\2656";
}

.piece-bishop:before {
  content: "\2657";
}

.piece-knight:before {
  content: "\2658";
}

.piece-gold-general:before {
  content: "\91DD";
}

.piece-silver-general:before {
  content: "\91D8";
}

.piece-lance:before {
  content: "\91CE";
}

.piece-pawn:before {
  content: "\91D7";
}

/* i really want this to be global */
