css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oper.ru") {

div.block, div#left, div#right, div#topbanner, div#headermenul, p.bottomp, p.bottomh, hr {display:none !important;}
#container {max-width: 85% !important;}
#headermenuu {margin-top: 10px !important;}
div#middle {border-width: 10px 1px 1px; border-color: #464646; border-style: solid; padding: 10px !important; margin-top: 10px !important;}
table.comment {border-width: 4px 1px 1px; border-color: #464646; border-style: solid; padding: 0px !important; margin-top: 5px !important;}
span.posted {color:#D0D0C0 !important;}
p.meta {white-space: nowrap;}
}
