css:
/* Change the background color /
body {
  background-color: #222;
}

/ Change the font family and color for headings /
h1, h2, h3, h4, h5, h6 {
  font-family: Arial, sans-serif;
  color: #fff;
}

/ Change the font size for paragraph text /
p {
  font-size: 16px;
}

/ Add a border to images /
img {
  border: 1px solid #ccc;
}

/ Change the link colors /
a {
  color: #ffa500;
}

/ Add a background color and padding to buttons /
button {
  background-color: #ffa500;
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  color: #fff;
  font-size: 16px;
  cursor: pointer;
}

/ Change the color of form inputs /
input[type="text"], input[type="email"], input[type="password"] {
  color: #333;
  border: 1px solid #ccc;
  padding: 5px;
  border-radius: 4px;
}

/ Change the background color and border of form submit buttons */
input[type="submit"] {
  background-color: #ffa500;
  border: none;
  border-radius: 4px;
  padding: 10px 20px;
  color: #fff;
  cursor: pointer;
}
