css:
@-moz-document domain(chat.openai.com)
{
/*–––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––*/

/*                        instagram: spr.mrc                             */

/*–––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––*/
/*–––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––*/

/*                        instagram: spr.mrc                             */

/*–––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––––*/
  
.md\:w-\[260px\] {
    width: 10%!important;
    backdrop-filter: blur(30px);
    padding: 0px !important;  
  background: linear-gradient(0deg, #000000, #11111100);
}
.items-end {  margin-left: 70px;}
html {    font-family: iranyekan;}
.text-gray-100 {color: #ddd!important;}

.text-sm {font-size: 1rem;line-height: 1.25rem;}

.hover\:bg-\[\#2A2B32\]:hover{ border-radius: 2em; background: #ffffff0a;}

.pr-14 { border: solid 0.5px #10a37f !important;}

.w-4 {color: #ddd!important;}


.px-3 {
    padding-left: 0.75rem;
    padding-right: 0.75rem!important;
}

#__next > div.overflow-hidden.w-full.h-full.relative.flex > div.dark.hidden.bg-gray-900.md\:flex.md\:w-\[260px\].md\:flex-col > div > div > nav > a:nth-child(7),
#__next > div.overflow-hidden.w-full.h-full.relative.flex > div.dark.hidden.bg-gray-900.md\:flex.md\:w-\[260px\].md\:flex-col > div > div > nav > a:nth-child(6),
#__next > div.overflow-hidden.w-full.h-full.relative.flex > div.dark.hidden.bg-gray-900.md\:flex.md\:w-\[260px\].md\:flex-col > div > div > nav > a:nth-child(5),
#__next > div.overflow-hidden.w-full.h-full.relative.flex > div.dark.hidden.bg-gray-900.md\:flex.md\:w-\[260px\].md\:flex-col > div > div > nav > a:nth-child(4),
#__next > div.overflow-hidden.w-full.h-full.relative.flex > div.dark.hidden.bg-gray-900.md\:flex.md\:w-\[260px\].md\:flex-col > div > div > nav > a:nth-child(3){display:none!important}
.border-b {    border-bottom-width: 0px;}
button,.w-4:hover {color: #10a37f!important;}

.text-gray-300 {margin-right:10px}

.duration-200:hover, .dark .btn-neutral:hover {color: #10a37f!important;}

.xl\:max-w-3xl{    max-width: 80%!important;}

.text-gray-300 {
  position: inherit;}
p,.items-start {
  direction: rtl!important;}
code, pre {
  direction: ltr!important;width:50%;}

#__next, #root { direction: rtl;}
.from-gray-800{ visibility: hidden;}
.rounded-sm { border-radius: 1em;}
.dark .dark\:shadow-\[0_0_15px_rgba\(0\2c 0\2c 0\2c 0\.10\)\] {
    background: transparent;
    backdrop-filter: blur(10px);
    border-radius: 2em;
border-color: #10a37f;
}
.duration-200, .dark .btn-neutral, .pr-14 {
    border-radius: 3em;
    transition-property: all;
    transition-duration: .5s;
    border: none;
    background: #ffffff0a;
}

.dark .btn-neutral {
    border-radius: 2em;
    backdrop-filter: blur(100px);
border-color: #10a37f!important;
}
.pr-7 {    padding-right: 50px;}

.dark .dark\:bg-\[\#444654\] {
    border-right: solid 5px #10a37f !important;
    box-shadow: inset -30px 0px 30px -30px #10a37f !important;}

.dark .dark\:bg-gray-800 {  
    box-shadow: inset -30px 0px 30px -30px #ddd !important;}

.lnXdpd{filter: drop-shadow(0px 0px 18px black);}
.from-gray-900{visibility: hidden;}

}
