css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://my.deviantart.com/messages/") {

}


body.mc-stacks div.mcbox-preview-stack div.talkmessage-comment, body.mc-stacks div.mcbox-full-stack div.talkmessage-comment {
background-color:#F0F5EE!important;
}

.mc-ctrl{
background-color:#DAE4D9!important;
background-image:url("http://st.deviantart.com/minish/gruzecontrol/gmtop.gif?1")!important;
background-repeat:repeat-x!important;
border-color:#A6B2A6 !important;
}

/* open reply */
.ch-ctrl {background-color:#DAE4D9!important;
border-color:#A6B2A6 !important;}

div.mcbox-full-reply i.trhack {background-color:#DAE4D9!important;
border-right:1px solid #A6B2A6 !important;}


.dd i {border-color:#DAE4D9 #F0F5EE #DAE4D9 #DAE4D9!important;}

.mcb-whoicon {background-color:transparent!important;}

.mcbox-inner-full-comment .talkmessage {background-color:#F0F5EE!important;}

.mcbox-full-poll .mcb-title {background-color:#F0F5EE!important; -moz-border-radius:5px 5px 5px 5px!important;}

.mcb-vote {background-color:#F0F5EE!important; -moz-border-radius:5px 5px 5px 5px!important; margin-bottom:10px!important;}

.voteresults {background-color:#F0F5EE!important; -moz-border-radius:5px 5px 5px 5px!important; margin-bottom:10px!important;}

.mcb-line {color:#89A08E!important;}

.mcb-line a {color:#89A08E!important;}

/* large icons in message stacks */
body.mc-stacks div.mcbox-full-stack div.av_sm .avatar, body.mc-stacks div.mcbox-preview-stack div.av_sm .avatar  {height:50px!important;}

/* buttons (original) & (reply) */
a.gmbuttonlil {
background-image:url("http://brgtt.dk/stylish/messagebuttonlil.gif")!important;
color:#899d8c!important;
text-shadow:#f9faf9 0px 1px 0px;
}

/* highlight blue */
body div.bubbleview div.messages div.mcbox-sel i.c i, body div.bubbleview div.messages div.mcbox-sel div.ch-ctrl {
border-color:#498091 !important;
}

body div.bubbleview div.mczone div.mcbox-sel-full-reply i.trhack {
border-right:2px solid #498091!important;
}
