css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.overclockers.ru")
{

html>body>div>div[align='center']:nth-of-type(2),
/* Flash-баннер в шапке */
#ad_ph_1,

/* Spacer */
body>div>img[src*='spacer'],

/* Реклама после статистики разгона в шапке */
body>div>table.navbg~div[align='center'],

/* Правый сайдбар */
td[width='76%'][valign='top']+td[width='12%'],
td[width='12%'][valign='top']~td[width='200'][valign='top'],

/*Оцените материал*/
map,map~table:first-of-type,
map~br:nth-of-type(1),map~br:nth-of-type(2),map~br:nth-of-type(3),map~br:nth-of-type(4),
map:nth-child(3)~br:nth-of-type(5),
map:nth-child(3)~br:nth-of-type(6),

/*После "Обсудить в конференции"*/
img[src*='m4']+a[href*='viewtopic']~br:nth-of-type(1),
img[src*='m4']+a[href*='viewtopic']~br:nth-of-type(2),

/*Убираем br перед средними колонками*/
div.nou td[width='50%'] br,

/*Реклама перед подвалом*/
body>div[align='center']:not(:first-child),body>div>center~table[height='1'],
body>div[align='center']:nth-of-type(1)>center,
/*Уменьшаем подвал*/
body>div>table:last-of-type div.light br:first-of-type,
body>div>table:last-of-type a[href*='republishing']~br,

/*Левый сайдбар*/
td[width='12%'][valign='top']>div[style*='137'],
td[width='12%'][valign='top'] object,
td[width='12%'][valign='top']>br,
td[width='12%'][valign='top']>style+a,
td[width='12%'][valign='top']>div[align='center'],

/*Реклама в колонках новостей*/
div.nou td[valign='top']>div[align='center'],
/*Объявления компаний (реклама)*/
div.nou br,
div.nou>table>tbody>tr:not(:nth-of-type(3)),

div.menu>div:nth-of-type(5),
html>body>div>table:nth-of-type(8),
html>body>div>table:nth-of-type(9),
div.light>center,

/*Maxlab*/
[src*='maxlab'],object[width='240'][height='400'],

/* Ещё реклама*/ 
.topadvert_div
{display: none !important}

/*Одинаковая ширина колонок новостей*/
div.nou>table>tbody>tr:nth-of-type(3)>td
{width:50% !important}

/*Отступ справа*/
td[width*='76'][valign='top'],
td[width='12%'][valign='top']+td
{padding-right: 10% !important}

/*Содержимое*/
[width='501'][height='60'],
div[style*='700px']>table[bgcolor='#dddddd'],
div[style*='700px']>table>tbody>tr>td:nth-of-type(2)>p:last-child>table,
div[style*='700px']>table>tbody>tr>td:nth-of-type(2)>p:last-child>br,
td[width*='76'][valign='top']>div[style*='490px'],
div[style*='700px']>center:first-child,
/*div[style*='700px']>p>table[width='100%'],*/
/*Переходы на новые строки*/
/*div[style*='700px']>p:last-of-type~br,*/
/*В самом начале*/
script[src*='ajax/a.js']~br,
/*div[style*='700px']>br,*/
/*В шапке*/
body>div>br,
/*На страницах статей*/
div[style*='700px']>p:last-of-type>table:nth-child(3)~br,
/*Два вертикальных баннера во втором параграфе*/
div[style*='700px']>h3~p:nth-of-type(2) table,
/*Пустая ячейка в лента новостей*/
div[style*='700px'] td[width='50']
{display: none}
/*Ширина*/
div[style*='700px']
{width: 100% !important;}

/*Статистика разгона*/
script[src*='database']+table,
/*Нужные отступы*/
a[href='/']~br
{display:block !important}

[id*='MarketGid'],
table[width='600']
{display: none !important}

[width='302'][height='245'],
[width='303'][height='245']
{display: none !important}

table[height='68'][width='100%'],
td[width='160'][valign='bottom']+td,
td[background*='m_out'][width='100%']>span,
a[href*='hp-promo'],a[href*='hp-promo']~br
{display: none !important}

/*Добавляем отступ после каждой таблицы на в средних колонках*/
div.nou>table>tbody>tr>td>table
{margin-bottom:1em !important;}

/* Отсутп сверху левого сайдбара */
td[width='12%'][valign='top']>table:first-of-type
{margin-top:7px !important}



}

@-moz-document url-prefix("http://forums.overclockers.ru")
{

table[width='100%'][height='68'],
[id*='direct'],#wrapfooter,
#pagecontent>div[align='center'],#pagecontent>div[align='center']~br,
.banner,
#wrapcentre>br[clear='all']+div[align='center'],
body>center,body>br,
body>img[src*='spacer']
{display: none !important}

#wrapcentre
{margin-top:0px !important;}

/*Одинаковая ширина левой части сообщений*/
#pagecontent [class*='row']>td:first-of-type
{min-width:170px !important;}

/*Отсутуп сверху*/
#mmenu
{margin-top:6px !important;}

#mmenu .gensmall
{margin:0px !important;}
}
