css:
@-moz-document domain("chess.com")
{
.board, .game-preview-component { 
  background-image: url("https://static.vecteezy.com/system/resources/previews/006/644/516/non_2x/official-chess-board-black-and-white-square-background-vector.jpg") !important;
  border: solid 15px #777777 !important;

}

.coordinate-dark {
  fill: #ffffff !important;
  }
  .coordinate-light {
    fill: #000000 !important;
  }
    div [style^="background-color"]
  {
    background-color: rgba(0, 255, 255, .5) !important;
  }
  .arrow {
    fill: rgba(0, 255, 255, .5) !important;
  }



}
