css:
@-moz-document domain(youtube.com) {
.ytp-scrubber-pull-indicator {
  /* hollow knight slider */
  background-color: #fff0 !important;
  height: 35px !important;
  width: 45px !important;
  background-image: url(https://media4.giphy.com/media/v1.Y2lkPTc5MGI3NjExNjExYjM5MDMxY2MwN2IwZDQ5ZjNlNjRlZDY2MGRjNjgwYzI1MGI2NSZjdD1z/Y8bAdBxtKREVcuDNyH/giphy.gif) !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  background-size: 43px, 80px !important;
  bottom: 18px !important;
  left: -10px !important;
  transform: rotate(0deg) !important;
  transform: scale(-1.5, 1.5) !important;
  border-radius: 30px !important;
}

.ytp-swatch-background-color {
  /* sliderball */
  background-color: #212a53 !important;
}

.ytp-volume-slider-handle,
.ytp-volume-slider-handle::after,
.ytp-volume-slider-handle::before,
.ytp-settings-menu .ytp-menuitem-toggle-checkbox,
.ytp-autonav-toggle-button,
.yt-spec-icon-badge-shape__badge,
.iron-selected /* some menu selections */ {
  /* some items bg */
  background-color: #212a53 !important;
}

.ytd-video-owner-renderer .yt-spec-button-shape-next--filled {
  color: #fff !important;
  background-color: #212a53 !important;
}

.iron-selected .yt-chip-cloud-chip-renderer {
  /* some menu selections */
  color: #fff !important;
}

.ytd-thumbnail-overlay-resume-playback-renderer {
  /* text and hyperlinks */
  background-color: #0b33e6 !important;
}

.yt-simple-endpoint {
  /* text and hyperlinks */
  color: #687dde !important;
}

.yt-core-attributed-string__link,
#owner-sub-count, 
#channel-name,
yt-formatted-string.ytd-channel-name .yt-simple-endpoint,
.ytd-video-meta-block .inline-metadata-item:nth-child(3n){
  /* text and hyperlinks */
  color: #5461a1 !important;
}

.ytd-channel-name .yt-simple-endpoint,
.ytd-comment-renderer,
ytd-author-comment-badge-renderer .ytd-channel-name {
  /* bug fix */
  color: #fff !important;
}

.ytd-c4-tabbed-header-renderer .iron-selected {
  background-color: #212129 !important;
  border-radius: 5px;
  border-bottom-left-radius: 0px;
  border-bottom-right-radius: 0px;
} 

/* logo */
#logo-icon {
  content: url("https://static.wikia.nocookie.net/hollowknight/images/4/49/Cut_Last_Stag_Sleeping.gif") !important;
  width: 70px !important;
  height: 50px !important;
  object-fit: cover !important;
  transform: translateY(-22px) !important;
}

.ytd-topbar-logo-renderer {
  transform: translateY(10px) !important;
  color: #687dde !important;
}

/* logo */
ytd-topbar-logo-renderer {
  width: 0% !important;
}

}
