@-moz-document url-prefix("https://workflowy.com") {
/* WorkFlowy White / RGB / Rainbow Dynamic Light Theme

Note: Changing to a different theme within WorkFlowy settings allows you to change the color of the top navigation menu bar and various pop-out menus created by WorkFlowy. For example, you can choose WorkFlowy's:
    • Dark theme
    • Default theme (light)
    • Vintage theme (dark top navigation menu bar, light pop-out menus) */
/* Background is from GIPHY, the Web's top site for free GIFs:

By Erica Anderson @ericaanderson
"I am on a quest to make fine art that moves."
https://giphy.com/ericaofanderson

A gallery of Erica Anderson's GIFs:
https://ericaofanderson.com

Support Erica Anderson on Patreon:
https://patreon.com/ericaofanderson

Follow Erica Anderson on:
https://facebook.com/ericaofanderson
https://instagram.com/ericaofanderson
https://twitter.com/ericaofanderson
https://ericaofanderson.tumblr.com/

Source:
https://giphy.com/gifs/gif-artist-glas-2017-ericaofanderson-26xBwKfFKiDkZ2aVa
Rainbow Orange GIF By Erica Anderson */
body {
    background-image: url("https://media2.giphy.com/media/v1.Y2lkPTc5MGI3NjExNmU1OTYwN2EzMjViYzE4NGFmNTNjMmYwOGFhNDU3MzQxNTIyOTAxNiZjdD1n/26xBwKfFKiDkZ2aVa/giphy.gif");
    background-color: #f2f2f2;
    transition: background-image 6.9s ease-in-out;
    background-attachment: fixed;
    background-size: cover;
    background-position: center center;
}
.page {
    background-image: url("https://live.staticflickr.com/65535/52812254787_5431a98ea3_o.png");
    background-color: rgba(255, 255, 255, 0.6);
    border-width: 1px;
    border-style: solid;
    border-color: hsl(185, 70%, 55%) !important;
    -moz-border-radius: 49px 49px 49px 49px;
    border-radius: 49px 49px 49px 49px;
    overflow-x: hidden;
}
.content {
    color: hsl(0, 0%, 0%);
}
.project.done .content a {
    color: #888;
}
.boardColumn,
.boardColumn div {
    background-color: rgba(123, 123, 123, 0.05);
    border-color: #6A6C6E;
}
.comment-container div {
    color: #95989A;
}

/* WorkFlowy Top Menu Bar*/
div.header svg,
.breadcrumbs a,
.searchBoxInput,
svg {
    color: hsl(185, 70%, 35%);
    fill: hsl(185, 70%, 35%);
}
div.homeButton svg,
a.bullet svg {
    color: hsl(185, 70%, 35%) !important;
    fill: hsl(185, 70%, 35%) !important;
}
.breadcrumbs a:hover {
    text-decoration-line: underline;
    text-decoration-style: wavy;
}
.searchBox div,
div.header,
div.flyout {
    border-color: hsl(185, 70%, 35%) !important;
}
.breadcrumbs a:hover,
.searchBoxInput:hover,
svg:hover {
    color: hsl(185, 100%, 55%);
    fill: hsl(185, 100%, 55%);
}
div.header svg:hover,
div.homeButton:hover svg,
a.bullet:hover svg {
    color: hsl(185, 100%, 55%) !important;
    fill: hsl(185, 100%, 55%) !important;
}

/* LINKS AND TAGS */
.content .contentLink,
.contentTag {
    text-decoration-style: dotted;
    color: hsl(200, 70%, 58%);
}
.content .contentLink:hover,
.contentTag:hover {
    text-decoration-line: underline;
    text-decoration-style: wavy;
    color: hsl(200, 100%, 48%);
}

/* BULLETS */
a.bullet > svg,
a.bullet > svg > path[d="M9 4 L4 9 L9 14 L14 9"],
a.bullet[href^="/#/v_"] > svg > circle,
.shared .bullet > svg,
.addedShared .bullet > svg,
[data-tid] .bullet > svg {
    fill: hsl(0, 0%, 0%);
}
/* CIRCLE IMMEDIATELY SURROUNDING BULLETS */
a.bullet > svg > circle {
    stroke: hsl(0, 0%, 0%);
}

/* EXPANDED LEFT-LINE */
.project.open > .children,
.project.open {
    border-left-color: hsl(185, 70%, 35%);
}

/* EXPANDED NON-HALOS ON HOVER */
.name > .bullet:hover,
a.bullet:hover {
    background-color: hsl(0, 0%, 70%);
}

/* COLLAPSED HALOS */
.collapsed a.bullet {
    background-color: hsl(0, 0%, 40%);
}

/* COLLAPSED HALOS ON HOVER */
.collapsed a.bullet:hover {
    background-color: hsl(0, 0%, 55%);
}

/* SHARED HALOS */
.shared > .name > .bullet {
    background-color: hsl(200, 65%, 70%);
}
/* SHARED HALOS ON HOVER */
.shared > .name > .bullet:hover {
    background-color: hsl(200, 100%, 70%);
}

/* EMBEDDED HALOS */
.addedShared > .name > .bullet {
    background-color: hsl(200, 65%, 70%);
}
/* EMBEDDED HALO ON HOVER */
.addedShared > .name > .bullet:hover {
    background-color: hsl(284, 62%, 80%);
}

/* ALL STYLES BULLETS/HALOS CODE */
/* CIRCLE IMMEDIATELY SURROUNDING BULLETS */
a.bullet > svg > circle {
    stroke-width: 1;
}

/* ALL-HALOS SHAPE */
.name > .bullet,
a.bullet {
    border-radius: 9px;
}

/* ALL-HALOS SHAPE ON HOVER*/
.name > .bullet:hover,
a.bullet:hover {
    border-radius: 7px;
}

/* EXPANDED NON-HALOS */
.name > .bullet,
a.bullet {
    background-color: transparent;
}

/* LIGHT THEMES text colors */
.c-red {
    color: #E02425;
}
.c-orange {
    color: #D03800;
}
.c-yellow {
    color: #9F580B;
}
.c-green {
    color: #057A55;
}
.c-teal {
    color: #118190;
}
.c-sky {
    color: #017AB8;
}
.c-blue {
    color: #1B64F2;
}
.c-purple {
    color: #7E3AF2;
}
.c-pink {
    color: #D613C1;
}
.c-gray {
    color: #64748B;
}

/* LIGHT THEMES text colors */
.c-red {
    color: #E02425;
    text-decoration-color: #E02425;
}
.c-orange {
    color: #D03800;
    text-decoration-color: #D03800;
}
.c-yellow {
    color: #9F580B;
    text-decoration-color: #9F580B;
}
.c-green {
    color: #057A55;
    text-decoration-color: #057A55;
}
.c-teal {
    color: #118190;
    text-decoration-color: #118190;
}
.c-sky {
    color: #017AB8;
    text-decoration-color: #017AB8;
}
.c-blue {
    color: #1B64F2;
    text-decoration-color: #1B64F2;
}
.c-purple {
    color: #7E3AF2;
    text-decoration-color: #7E3AF2;
}
.c-pink {
    color: #D613C1;
    text-decoration-color: #D613C1;
}
.c-gray {
    color: #64748B;
    text-decoration-color: #64748B;
}

/* LIGHT THEMES highlighter colors */
.bc-red {
    color: black !important;
    background-color: #FAD5D5 !important;
}
.bc-orange {
    color: black !important;
    background-color: #FCD9BD !important;
}
.bc-yellow {
    color: black !important;
    background-color: #FCE969 !important;
}
.bc-green {
    color: black !important;
    background-color: #BCF0DA !important;
}
.bc-teal {
    color: black !important;
    background-color: #BCF0F0 !important;
}
.bc-sky {
    color: black !important;
    background-color: #BAE6FD !important;
}
.bc-blue {
    color: black !important;
    background-color: #C3DDFD !important;
}
.bc-purple {
    color: black !important;
    background-color: #DCD7FE !important;
}
.bc-pink {
    color: black !important;
    background-color: #FBD7FE !important;
}
.bc-gray {
    color: black !important;
    background-color: #E2E8F0 !important;
}

.project.done .c-red,
.project.done .c-orange,
.project.done .c-yellow,
.project.done .c-green,
.project.done .c-teal,
.project.done .c-sky,
.project.done .c-blue,
.project.done .c-purple,
.project.done .c-pink,
.project.done .c-gray,
.project.done .bc-red,
.project.done .bc-orange,
.project.done .bc-yellow,
.project.done .bc-green,
.project.done .bc-teal,
.project.done .bc-sky,
.project.done .bc-blue,
.project.done .bc-purple,
.project.done .bc-pink,
.project.done .bc-gray {
    color: #bbb !important;
    text-decoration-color: #bbb;
}

.project.done .bc-red,
.project.done .bc-orange,
.project.done .bc-yellow,
.project.done .bc-green,
.project.done .bc-teal,
.project.done .bc-sky,
.project.done .bc-blue,
.project.done .bc-purple,
.project.done .bc-pink,
.project.done .bc-gray {
    background-color: #E7E7E7 !important;
}

.contentMatch {
    color: black !important;
    background-color: hsl(60, 100%, 80%) !important;
}

/* AD REMOVAL */
.getMoreSpaceButton {
    display: none;
}
#bottomLinks {
    display: none;
}
}
