css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vott.ru") {

a {color: #FFA500 !important; text-decoration: none !important;}
a.title {background: none !important;}
a.user_ban {color: #D0D0C0 !important; font-weight: bold !important; text-decoration: line-through !important;}
a.user_m {color: #FF170F !important; font-weight: bold !important;}
a.user_ro {color: #D0D0C0 !important; font-weight: bold !important;}
a.user_self {color: #E0E080 !important; font-weight: bold !important;}
a.vtopku {color: #FF170F !important; font-weight: bold !important;}
a:hover {color: #FFFFFF !important;}

body {background-color: #000000 !important; color: #000000 !important;}

div#loggedinLinks {padding-top: 5px !important;}
div#loggedinLinks strong {color: #D0D0C0 !important;}
div.announcement {background: #404040 !important; border-color: #D0D0C0 !important; color: #D0D0C0 !important;}
div.comment_body {color: #D0D0C0 !important;}
div.comment_form {border-style: solid !important; border-color: #464646 !important; border-width: 4px 1px 1px !important; background-color: #000000 !important;}
div.comment_hightlight {background-color: #000000 !important;}
div.comment_info, div.comment_info_l, div.comment_info_r {color: #D0D0C0 !important; font-weight: bold !important;}
div.comment_info_wrapper {border-style: none !important;}
div.comment_wrapper {background-color: #404040 !important; color: #D0D0C0 !important;}
div.comment_wrapper, div.comment_wrapper2, div.form_wrapper {border-style: solid !important; border-color: #464646 !important; border-width: 4px 1px 1px !important; margin-bottom: 4px !important; margin-top: 0px !important;}
div.comment_wrapper2 {background-color: #303030 !important; color: #D0D0C0 !important;}
div.content_wide {margin-right: 0px !important; margin-left: 0px !important;}
div.content_wrapper {margin-right: 0px !important; padding-top: 0px !important; padding-bottom: 0px !important; color: #D0D0C0 !important;}
div.errorExplanation {background: #000000 !important; color: #D0D0C0 !important;}
div.footer {margin-left: inherit !important; margin-right: inherit !important; max-width: inherit !important;}
div.footer td {padding: 0px 15px 0px 15px !important; border-right: 1px solid #464646 !important;}
div.form_wrapper {background-color: #000000 !important; font-weight: bold; color: #D0D0C0 !important;}
div.header {background: #303030 !important; height: 30px; margin-top: 5px !important; border: solid 1px #D0D0C0 !important; padding-bottom: 2px !important; padding-top: 2px !important; padding-left: 2px !important; padding-right: 2px !important;}
div.header a {background: none !important;}
div.info, div.info2, div.info_small {color: #D0D0C0 !important;}
div.logo { background: #FFFFFF !important; position: absolute; left: 8px; top: 5px !important; width: 84px; height: 30px; border: 1px solid #D0D0C0 !important; padding-top: 2px !important; padding-bottom: 2px !important; padding-left: 2px !important; padding-right: 2px !important;}
div.navigation {border-style: none !important; background-color: #000000 !important;}
div.navigation a.current {background-color: #464646 !important; border-style: none !important;}
div.navigation a:hover {background-color: #000000 !important; border-style: none !important;}
div.paginator a {color: #00a; border: 1px solid #D0D0C0 !important;}
div.paginator a:hover {background: #404040 !important;}
div.popup_menu_container div.popup_menu {background: #000000 none repeat scroll 0 0 !important; border: 1px solid #D0D0C0 !important;}
div.popup_menu_container div.popup_menu a {color: #FF170F !important;}
div.popup_menu_container div.popup_menu a:hover {color: #FF170F !important; background: #404040 !important;}
div.spacer_line {border-bottom: 1px solid #D0D0C0 !important; margin-left: inherit !important;}
div.top_options, div.top_options a {font-weight: bold !important;}
div.vote {background-color: #404040 !important; border: 1px solid #D0D0C0 !important;}
div.vote a {font-weight: bold !important;}

form#quick_search_form, form#top_login_form {margin-right: 3px !important; padding-top: 3px !important;}
h2#underline, h2 {border-color: #D0D0C0 !important; color: #D0D0C0 !important;}
li {color: #D0D0C0 !important;}
pre {color: #000000 !important;}
span#tag_red {color: #FF170F !important;}
span.quote {color: #E0E080 !important;}
#spinner div {color: #000000 !important; border: 2px solid #FFA500 !important;} 
td {background: #000000 !important; color: #D0D0C0 !important;}
}
