body {
    background-image: url("https://media4.giphy.com/media/v1.Y2lkPTc5MGI3NjExZDlhMDhlYTU4NTcyNzcyNWQ5OGM0YjkwMDY1OGMxMzhmYTU5MmY3MSZjdD1n/WtJmPsQrPhXoPHptG9/giphy.gif");
    background-color: #2d160b;
    transition: background-image 6.9s ease-in-out;
    background-attachment: fixed;
    background-size: cover;
    background-position: center center;
}
.page {
    background-image: url("https://live.staticflickr.com/65535/52812254787_5431a98ea3_o.png");
    background-color: rgba(0, 0, 0, 0.6);
    border-width: 1px;
    border-style: solid;
    border-color: hsl(30, 100%, 65%) !important;
    -moz-border-radius: 49px 49px 49px 49px;
    border-radius: 49px 49px 49px 49px;
    overflow-x: hidden;
}
.content {
    color: white;
}
.project.done .content a {
    color: #ccc;
}
.boardColumn,
.boardColumn div {
    background-color: rgba(0, 0, 0, 0.8);
    border-color: #6A6C6E;
}
.comment-container div {
    color: #95989A;
}

/* WorkFlowy Top Menu Bar*/
div.header svg,
.breadcrumbs a,
.searchBoxInput,
svg {
    color: hsl(30, 100%, 65%);
    fill: hsl(30, 100%, 65%);
}
div.homeButton svg,
a.bullet svg {
    color: hsl(30, 100%, 65%) !important;
    fill: hsl(30, 100%, 65%) !important;
}
.breadcrumbs a:hover {
    text-decoration-line: underline;
    text-decoration-style: wavy;
}
.searchBox div,
div.header,
div.flyout {
    border-color: hsl(30, 100%, 65%) !important;
}
.breadcrumbs a:hover,
.searchBoxInput:hover,
svg:hover {
    color: hsl(30, 100%, 50%);
    fill: hsl(30, 100%, 50%);
}
div.header svg:hover,
div.homeButton:hover svg,
a.bullet:hover svg {
    color: hsl(30, 100%, 50%) !important;
    fill: hsl(30, 100%, 50%) !important;
}

/* LINKS AND TAGS */
.content .contentLink,
.contentTag {
    text-decoration-style: dotted;
    color: hsl(30, 100%, 50%);
}
.content .contentLink:hover,
.contentTag:hover {
    text-decoration-line: underline;
    text-decoration-style: wavy;
    color: hsl(30, 100%, 70%);
}

/* BULLETS */
a.bullet > svg,
a.bullet > svg > path[d="M9 4 L4 9 L9 14 L14 9"],
a.bullet[href^="/#/v_"] > svg > circle,
.shared .bullet > svg,
.addedShared .bullet > svg,
[data-tid] .bullet > svg {
    fill: hsl(24, 100%, 50%);
}
/* CIRCLE IMMEDIATELY SURROUNDING BULLETS */
a.bullet > svg > circle {
    stroke: hsl(24, 100%, 50%);
}

/* EXPANDED LEFT-LINE */
.project.open > .children,
.project.open {
    border-left-color: hsl(30, 100%, 50%);
}

/* EXPANDED NON-HALOS ON HOVER */
.name > .bullet:hover,
a.bullet:hover {
    background-color: hsl(0, 0%, 70%);
}

/* COLLAPSED HALOS */
.collapsed a.bullet {
    background-color: hsl(0, 0%, 40%);
}

/* COLLAPSED HALOS ON HOVER */
.collapsed a.bullet:hover {
    background-color: hsl(0, 0%, 55%);
}

/* SHARED HALOS */
.shared > .name > .bullet {
    background-color: hsl(200, 65%, 70%);
}
/* SHARED HALOS ON HOVER */
.shared > .name > .bullet:hover {
    background-color: hsl(200, 100%, 70%);
}

/* EMBEDDED HALOS */
.addedShared > .name > .bullet {
    background-color: hsl(200, 65%, 70%);
}
/* EMBEDDED HALO ON HOVER */
.addedShared > .name > .bullet:hover {
    background-color: hsl(284, 62%, 80%);
}

/* ALL STYLES BULLETS/HALOS CODE */
/* CIRCLE IMMEDIATELY SURROUNDING BULLETS */
a.bullet > svg > circle {
    stroke-width: 1;
}

/* ALL-HALOS SHAPE */
.name > .bullet,
a.bullet {
    border-radius: 9px;
}

/* ALL-HALOS SHAPE ON HOVER*/
.name > .bullet:hover,
a.bullet:hover {
    border-radius: 7px;
}

/* EXPANDED NON-HALOS */
.name > .bullet,
a.bullet {
    background-color: transparent;
}

/* DARK THEMES text colors */
.c-red {
    color: #F98080;
    text-decoration-color: #F98080;
